import {initialize_latin_square} from "/scripts/experiment-properties/balancing/generators/latin_square_generator.js";
import {initialize_random_order} from "/scripts/experiment-properties/balancing/generators/random_generator.js";
import {initialize_blocked_latin_square} from "/scripts/experiment-properties/balancing/generators/blocked_latin_square_generator.js";
import {innitialize_custom_order} from "/scripts/experiment-properties/balancing/generators/custom_order_generator.js";
import { BALANCING_TYPES } from "/config/balancing-config.js";
import { EXPERIMENTS } from "/config/experiments-config.js";

export {balance_subconditions};

/**
 * Returns the balanced order of the subconditions given balancing type,
 * experiment, and number of subconditions.
 *
 * @param {string}     		balancing_type
 * @param {experiment_name} name of experiment
 * @param {dataset_length}  length of dataset AKA number of subconditions
 * @param {dataset} 		array of subconditions
 * @param {block_attribute} attribute we wish to block by
 *
 * @return {[]}		  		array of indices, representing each subcondition in the dataset.
 */
function balance_subconditions(balancing_type, experiment_name, dataset_length, dataset=null, block_attribute="block", custom_order_string=null){

	// BALANCING_TYPES constant from /config/balancing-config.js
	if (BALANCING_TYPES[balancing_type] === undefined) {
		throw Error(balancing_type + " balancing type is not supported.");
	}

	// EXPERIMENTS constant from /config/experiments-config.js
	if (EXPERIMENTS[experiment_name]["balancing_type"].includes(balancing_type)){
		let balanced_order = []
		switch (balancing_type) {
			case "latin_square":
				balanced_order = initialize_latin_square(dataset_length);
				break;
			case "random":
				balanced_order = initialize_random_order(dataset_length);
				break;
			case "blocked_latin_square":
				balanced_order = initialize_blocked_latin_square(dataset_length, dataset, block_attribute);
				break;
			case "custom": innitialize_custom_order(balanced_order, dataset, block_attribute, custom_order_string)
				break;
			case "none":
				balanced_order = Array.apply(null, {length: dataset_length}).map(Number.call, Number);
				break;
			default:
				throw Error(balancing_type + " has not been defined.");
		}
		return innitialize_custom_order(balanced_order, dataset, block_attribute, custom_order_string); //only applies if custom order is present in the URL
	}
}

export {shuffle, reorder}

// modified from https://github.com/sindresorhus/array-shuffle
// shuffle array in place
function shuffle(arr) {
	var len = arr.length
	while (len) {
		var rnd = Math.floor(Math.random() * len--)
		var tmp = arr[len]
		arr[len] = arr[rnd]
		arr[rnd] = tmp
	}
	return arr
}

/**
 * 
 * @param {Array} arr we are reording
 * @param {Array} index for the reordered array
 * @returns {Array}
 */
function reorder(arr, index){
	let reordered_array = new Array(index.length);
	for(let i = 0; i < index.length; i++){
		let new_index = index[i];
		reordered_array[i] = arr[new_index]
	}
	return reordered_array;
}

/**
 * Gets the remaining indices of the array from a given array of indices
 * It is assumed indices in curr_indices are within range of the array
 * 
 * @param {Array} curr_indices  array of current indices
 * @param {number} n 			size of the array
 * @returns {Array}
 */
export function get_remaining_elements(curr_indices, n){
	let remaining_indices = []
	for(let i = 0; i < n; i++){
		if(!curr_indices.includes(i)){
			remaining_indices.push(i);
		}
	}
	return remaining_indices;
}

/**
 * 
 * @param {Array} array Array of indices and * for any index 
 * @returns {Array} 
 */
export function get_any_array(a){
	let n = a.length;
	let generated_array = new Array(n);
	let curr_indices = [];
	let number_indices = new Set();
	for (let i = 0; i < a.length; i++){
		if(a[i] != '*'){
			curr_indices.push(a[i]);
			// Assign fixed indices to their final position in the generated array
			generated_array[i] = a[i];
			number_indices.add(i)
		}
	}

	// Shuffle indices so remaining indices are in 'any' order
	let remaining_indices = shuffle(get_remaining_elements(curr_indices, n));

	// Place the remaining indices into the available spots in the generated array
	let j = 0;
	for (let i = 0; i < n; i++){
		if(!number_indices.has(i)){
			generated_array[i] = remaining_indices[j];
			j++;
		}
	}

	return generated_array;
}

/**
 * Gets any array of indices and '*' and fills the '*' with any valid index in the array
 * @param {Array} a 
 * @returns 
 */
export function get_any_array_repeated(a){
	let n = a.length;
	let generated_array = new Array(n);
	let curr_indices = [];
	let number_indices = new Set();
	for (let i = 0; i < a.length; i++){
		if(a[i] != '*'){
			curr_indices.push(a[i]);
			// Assign fixed indices to their final position in the generated array
			generated_array[i] = a[i];
			number_indices.add(i)
		}
	}
	
	for (let i = 0; i < n; i++){
		if(!number_indices.has(i)){
			generated_array[i] = getRandomInt(0, n)
		}
	}

	return generated_array;
}
 
/**
 * https://stackoverflow.com/questions/1527803/generating-random-whole-numbers-in-javascript-in-a-specific-range
 * Returns a random integer between min (inclusive) and max (inclusive).
 * The value is no lower than min (or the next integer greater than min
 * if min isn't an integer) and no greater than max (or the next integer
 * lower than max if max isn't an integer).
 * Using Math.round() will give you a non-uniform distribution!
 */
function getRandomInt(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min + 1)) + min;
}

// Generating all permutations of array
// https://stackoverflow.com/questions/40654895/javascript-generating-all-permutations-of-an-array
export function get_permutation(array) {
    function p(array, temp) {
        var i, x;
        if (!array.length) {
            result.push(temp);
        }
        for (i = 0; i < array.length; i++) {
            x = array.splice(i, 1)[0];
            p(array, temp.concat(x));
            array.splice(i, 0, x);
        }
    }

    var result = [];
    p(array, []);
    return result;
}

export function generate_index_array(n){
	let array = [];
	for(let i = 0; i < n; i++){
		array.push(i);
	}
	return array;
}


/**
 * https://stackoverflow.com/questions/54775455/heaps-algorithm-javascript
 * Heaps algorithm to permute an array
 * @param {*} array array to get permutations 
 * @param {*} n size of array
 * @param {*} results 
 * @returns 
 */
export function heapsPermute(array, n, results = []) {
	n = n || array.length;
	if (n === 1) {
		results.push(array.slice());
	} else {
		for (var i = 1; i <= n; i += 1) {
		heapsPermute(array, n - 1, results);
		if (n % 2) {
			var j = 1;
		} else {
			var j = i;
		}
		function swap(array, pos1, pos2) {
			var temp = array[pos1];
			array[pos1] = array[pos2];
			array[pos2] = temp;
		};
		swap(array, j - 1, n - 1);
		}
	}
	return results;
}

import { shuffle, reorder } from "../balancing_helpers/balancing_helper.js"
export {initialize_blocked_latin_square, innitialize_custom_latin_square}

/**
 * Returns the balanced order of the subconditions in latin square order by block attribute,
 *
 * @param {dataset_length}  length of dataset AKA number of subconditions
 * @param {dataset} 		array of subconditions
 * @param {block_attribute} attribute we wish to block by
 *
 * @return {[]}		  		array of indices, representing each subcondition in the dataset.
 */
function initialize_blocked_latin_square(dataset_length, dataset, block_attribute) {

    // initialize an accumulator with the first subcondition's block_attribute
    let attribute_accumulator = [dataset[0][block_attribute]];

    // check other subconditions, find other values for the block_attribute
    for (let i = 0; i < dataset_length; i++) {
        if (!attribute_accumulator.includes(dataset[i][block_attribute])) {
            attribute_accumulator.push(dataset[i][block_attribute])
        }
    }

    // add subcondition numbers as attributes
    for (let i = 0; i < dataset_length; i++) {
        dataset[i].subcondition_number = i;
    }

    // shuffle the attributes and the dataset
    shuffle(attribute_accumulator);
    shuffle(dataset);

    return order_latin_square(attribute_accumulator, dataset, block_attribute);
}

/**
 * Returns the balanced order of the subconditions in latin square order by block attribute
 *
 * @param {array}     		block_order we wish the block attribute to be
 * @param {assoc} 		    array of subconditions
 * @param {string}          attribute we wish to block by
 * @param {boolean}         flag to determine if we allow repeating or insuficient blocks
 * @return {[]}		  		array of indices, representing each subcondition in the dataset.
 */ 
function innitialize_custom_latin_square(block_order, dataset, block_attribute, repeating_blocks){
    //console.log("Entered innitialize_custom_latin_square")
    // initialize an accumulator with the first subcondition's block_attribute
    let attribute_accumulator = [dataset[0][block_attribute]];
    let dataset_length = dataset.length;
    // check other subconditions, find other values for the block_attribute
    for (let i = 0; i < dataset_length; i++) {
        if (!attribute_accumulator.includes(dataset[i][block_attribute])) {
            attribute_accumulator.push(dataset[i][block_attribute])
        }
    }
    if (!repeating_blocks && block_order.length != attribute_accumulator.length) throw "Parsed Order Length does not match block attribute";

    // add subcondition numbers as attributes
    for (let i = 0; i < dataset_length; i++) {
        dataset[i].subcondition_number = i;
    }
    // set attribute order according to block order
    attribute_accumulator = reorder(attribute_accumulator, block_order, dataset_length);
    console.log(attribute_accumulator)
    // shuffle the  dataset
    shuffle(dataset);

    return order_latin_square(attribute_accumulator, dataset, block_attribute);
}

function order_latin_square(attribute_accumulator, dataset, block_attribute){
    // create a new multidimensional array based on groups of subconditions, 
    // with the size of the attribute accumulator to be the variant
    var reordered_dataset_grid = [...Array(attribute_accumulator.length)].map(e => Array(attribute_accumulator.length));


    for (let i = 0; i < dataset.length; i++) {
        // loop through dataset

        for (let j = 0; j < attribute_accumulator.length; j++) {
            // loop through shuffled attributes
            // if there is a match to the shuffled attributes, collect them one by one
            if (dataset[i][block_attribute] === attribute_accumulator[j]) {
                reordered_dataset_grid[j][i] = dataset[i];
            }
        }
    }

    // log them & collect them in a new array
    let reordered_dataset = [];
    for (let i = 0; i < reordered_dataset_grid.length; i++) {
        // get size of inner array
        var innerArrayLength = reordered_dataset_grid[i].length;
    
        // loop that inner array
        for (let j = 0; j < innerArrayLength; j++) {
            if (typeof reordered_dataset_grid[i][j] === 'object' && reordered_dataset_grid[i][j] !== null) {
                reordered_dataset.push(reordered_dataset_grid[i][j]);
            }
        }
    }

    // double check that the order works
    // reordered_dataset.forEach((e) => {
    //     console.log(JSON.stringify(e));
    // });

    // add the subcondition numbers.
    let new_order = []
    reordered_dataset.forEach((e) => {
        new_order.push(e["subcondition_number"]);
    });

    console.log("blocked latin square: ", new_order);


    // resort the dataset according to subcondition numbers, as it has been shuffled by previous functions.
    dataset.sort((a,b) => {
           return a.subcondition_number - b.subcondition_number;
    });

	return new_order;
}

import { get_permutation, heapsPermute, generate_index_array, reorder} from "../balancing_helpers/balancing_helper";

/**
 * Counter Balances a given Blocked Latin Square Order by ordering the subconditions within the blocks
 * according to a given Counter Balance Order. We can 
 * 
 * @param {number[]} blocked_order      Blocked Latin Square Order
 * @param {number} block_size           Number of Subconditions Per Block
 * @param {number} balance_order_type   Which Counter Balanced Order we want to Counter Balance by.
 *                                      The number of types is equal to the number of permutations a block can have
 * @param {number} num_blocks           Number of Blocks in the Blocked Order we want to Counter Balance
 * @returns {number[]}
 */
export function generate_counter_balanced_order(blocked_order, block_size, balance_order_type, num_blocks_to_cb){
    let num_blocks = blocked_order.length / block_size;
    if(num_blocks_to_cb > num_blocks) throw Error("Number of Blocks to Counter Balance larger than Number of Blocks in Condition");
    if(balance_order_type > math.factorial(num_blocks)) throw Error("Invalid Balanced Order Type");
    if(num_blocks_to_cb == null){
        num_blocks_to_cb = num_blocks;
    }
    let block_size_array = generate_index_array(block_size)
    //let permutations = get_permutation(block_size_array); 
    // We use heaps permute to get a specific order of permutations where the first element changes before the next elements
    let permutations = heapsPermute(block_size_array, block_size)
    //console.log(permutations)
    let counter_balanced_order = counter_balance_blocks(blocked_order, block_size, permutations, balance_order_type, num_blocks_to_cb)
    return counter_balanced_order;
}

/**
 * 
 * @param {number[]} blocked_order      blocked order generated by blocked latin square
 * @param {number} block_size           block size of the condition
 * @param {number[]} permutations       array of all permutations for a blocl
 * @param {number} balance_order_type   which permutation to start with
 * @param {number} num_blocks_to_cb     number of blocks to counter balance, rest of blocks are left in original blocked order (random)
 * @returns {number[]}
 */
function counter_balance_blocks(blocked_order, block_size, permutations, balance_order_type, num_blocks_to_cb){
    let balanced_order = [];
    let num_blocks = blocked_order.length / block_size;
    for(let curr_block_num = 0; curr_block_num < num_blocks; curr_block_num++){
        let curr_block_start_index = block_size * curr_block_num;
        let curr_block_end_index = curr_block_start_index + block_size;
        let curr_block = blocked_order.slice(curr_block_start_index, curr_block_end_index)
        if(curr_block_num < num_blocks_to_cb){
            let sorted_block = curr_block.sort(function(a, b) {return a - b;});
            let permutation_index = (balance_order_type + curr_block_num) % permutations.length;
            //console.log(permutations[permutation_index])
            let balanced_block = reorder(sorted_block, permutations[permutation_index])
            //console.log(balanced_block)
            balanced_order = balanced_order.concat(balanced_block)
        }
        else{
            balanced_order = balanced_order.concat(curr_block)
        }
    }
    //console.log(balanced_order)
    return balanced_order;
}

import { innitialize_custom_latin_square } from "./blocked_latin_square_generator.js";
import { get_any_array, get_any_array_repeated } from "../balancing_helpers/balancing_helper.js";
export { innitialize_custom_order }

const BLOCK_DIGIT = 'b';
const ANY_NUMBER_DIGIT = '*';
const TWO_DIGIT_MODE_DIGIT_0 = '2';
const TWO_DIGIT_MODE_DIGIT_1 = 'd';
const REPEAT_MODE_DIGIT = 'r';
const COUNTER_BALANCE_DIGIT = 'c';

/**
 * Documentation: https://docs.google.com/document/d/1EQb3qFepxX-nbXKM2CqBSqxrDCj5sYL8z61RnrXry88/edit#heading=h.7mwcmrbuwnzo
 * 
 * Reads from the URL directly to avoid having to route the parameters for each experiment (can be changed in the future for consistency)
 * 
 * 
 * @param {number[]}          balanced_order given by experiment's order in balancing_controller.js
 * @param {dataset} 		      array conditiond dataset
 * @param {string}            block_attribute we wish to block by in custom blocked latin square
 * @param {string}            custom_order_string custom order string routed to params
 * @return  {number[]}        new subcondition order or original balanced order if custom subcondition order fails
 * @author Jose Navarro
**/
function innitialize_custom_order(balanced_order, dataset=null, block_attribute=null, custom_order_string=null){
    // Check for custom subcondition order in URL
    // const PARAMS = new URLSearchParams(location.search);
    // let custom_order_string = PARAMS.get('order');
    if(custom_order_string == null) return balanced_order; // default case
    // Flag checking
    let digit_mode = "single_digit_mode";    // default single-digit mode
    let blocking_mode = "none";              // default blocking mode. Subcondition order input is manual
    let conuter_balance = false;             // flag for counter balancing
    let allow_repeats = false;               // flag to exlude repeated subconditions by default
    let num_blocks_to_cb;                    // number of counter balanaced blocks for 
    // order c (blocks to counter balance) b 2d r
    try{
      if(custom_order_string[0] == COUNTER_BALANCE_DIGIT){
        conuter_balance = true;
        custom_order_string = custom_order_string.slice(1);
        // Check if we only counter balance certain conditions
        if(!isNaN(Number(custom_order_string[0]))){
          counter_balance_blocks_n = Number(custom_order_string[0]);
        }
      }
      if(custom_order_string[0] == BLOCK_DIGIT){
        blocking_mode = "latin_square";
        custom_order_string = custom_order_string.slice(1);
      }
      if (custom_order_string[0] == TWO_DIGIT_MODE_DIGIT_0 && custom_order_string[1] == TWO_DIGIT_MODE_DIGIT_1){
        digit_mode = "two_digit_mode";
        custom_order_string = custom_order_string.slice(2);
      }
      if(custom_order_string[0] == 'r'){
        allow_repeats = true;
        custom_order_string = custom_order_string.slice(1);
      }
      if (custom_order_string.length == 0) throw "Custom Order Not Found.";
    }
    catch(err){
      console.log("Custom Subcondition Order Flag Parsing Error: " + err);+
      console.log("Defaulting to balanced order");
      return balanced_order;
    }
    // Digit parsing
    let parsed_digits = [];
    try{
      switch(digit_mode){
        case "single_digit_mode":
          parsed_digits = single_digit_parsing(custom_order_string, balanced_order, allow_repeats);
          break;
        case "two_digit_mode":
          parsed_digits = two_digit_parsing(custom_order_string, balanced_order, allow_repeats);
          break;
        default:
          throw "Digit Mode error";
      }
    }
    catch(err){
      console.log("Custom Subcondition Order Digit Parsing Error: "+ err);
      console.log("Defaulting to balanced order");
      return balanced_order;
    }
    console.log("Parsed Digits " + parsed_digits);
    if(parsed_digits.includes(ANY_NUMBER_DIGIT) && !allow_repeats){
      parsed_digits = get_any_array(parsed_digits);
    }
    else if(parsed_digits.includes(ANY_NUMBER_DIGIT) && allow_repeats){
      parsed_digits = get_any_array_repeated(parsed_digits);
    }
    // Blocking
    try{
      let custom_order = [];
      switch(blocking_mode){
        case "none":
          if(!allow_repeats && parsed_digits.length != balanced_order.length) throw "Length does not Match, was given " + parsed_digits.length + " instead of " +  balanced_order.length;
            custom_order = parsed_digits;  
          break;
        case "latin_square":
          custom_order = innitialize_custom_latin_square(parsed_digits, dataset, block_attribute, allow_repeats);
      }

      if(conuter_balance){
        custom_order = generate_counter_balanced_order(custom_order, block_size, balance_order_type, num_blocks_to_cb);
      }

      console.log("!!Custom Subcondition Order!!");
      return custom_order;
    }
    catch(err){
      console.log("Custom Subcondition Order Blocking Error: "+ err);
      console.log("Defaulting to balanced order");
      return balanced_order;
    }
  }
  

  /**
   * 
   * @param {*} custom_order_string 
   * @param {*} balanced_order 
   * @param {*} allow_repeats 
   * @returns 
   */
  function single_digit_parsing(custom_order_string, balanced_order, allow_repeats){
    let parsed_digits = [];
    let seen = new Set();
    let total_length = custom_order_string.length;
    let normal_length = balanced_order.length;
    for (let i = 0; i < custom_order_string.length; i++){
      let char = custom_order_string[i];
      if(char == ANY_NUMBER_DIGIT){
        parsed_digits.push(char);
      }
      else{
        let index = Number(custom_order_string[i]);
        if(isNaN(index)) throw "Index is NaN";
        if(index < 0 || index >= normal_length) throw "Index " + index +  " out of bounds";
        if(!allow_repeats) if(seen.has(index)) throw "Repeated Index";
        seen.add(index);
        parsed_digits.push(index);
      }
    }
    return parsed_digits;
  }

  /**
   * 
   * @param {*} custom_order_string 
   * @param {*} balanced_order 
   * @param {*} allow_repeats 
   * @returns 
   */
  function two_digit_parsing(custom_order_string, balanced_order, allow_repeats){
    let parsed_digits = [];
    let seen = new Set();
    let total_length = custom_order_string.length;
    let normal_length = balanced_order.length
    console.log(custom_order_string)
    if(total_length % 2 != 0) throw "Two Digit Mode was given non pair number of digits." 
    total_length = Math.floor(total_length / 2);
    for (let i = 0; i < custom_order_string.length-1; i+=2){
      let digit_1 = custom_order_string[i];
      let digit_2 = custom_order_string[i+1];

      if(digit_1 == ANY_NUMBER_DIGIT && digit_2 == ANY_NUMBER_DIGIT){
        parsed_digits.push(ANY_NUMBER_DIGIT);
      }
      else{
        let index = Number(digit_1+digit_2);
        if(isNaN(index)) throw "Index is NaN";
        if(index < 0 || index >= normal_length) throw "Index " + index +  " out of bounds";
        if(!allow_repeats) if(seen.has(index)) throw "Repeated Index";
        seen.add(index);
        parsed_digits.push(index);
  
      }
    }
    return parsed_digits;
  }

import { shuffle } from "../balancing_helpers/balancing_helper.js"

export { initialize_latin_square }

/**
 * Creates a random row generating function meeting the lating square restriction
 * usage:
 *		 sampler = latinCube(someRow)
 *		 newRow = sampler() | newRow = sampler(row)
 * @param	 {Array}		row samples to be randomized
 * @returns {Function} row generating function
 */
function latin_square (row) {
	var sN = row.length,
			rowCount = 0

	// prepare array of row and col indices for pre-sorting
	var hSort = shuffle(sequence(sN)),
			vSort = shuffle(hSort.slice())

	return function nextRow (countORtarget) {
		if (rowCount === sN) return countORtarget = null
		var target = Array.isArray(countORtarget) ? countORtarget
			: (countORtarget >= 0) ? Array(countORtarget)
			: Array(sN)
		if (target.length > sN) target.length = sN

		for (var i = 0; i < target.length; ++i) {
			var idx = hSort[i] + vSort[rowCount]
			if (idx >= sN) idx -= sN
			target[i] = row[idx]
		}
		rowCount++

		return target
	}
}
function sequence(n) {
	for (var i = 0, a=Array(n); i < n; ++i) a[i] = i
	return a
}

function initialize_latin_square(size){
    var array = Array.apply(null, {length: size}).map(Number.call, Number);
    var sampler = latin_square(array);
    var row = sampler();

   	console.log("Latin square: " + row);
    return row; 
}

export {initialize_random_order}

function initialize_random_order(length) {

	let order = [];

	for (let i = 0; i < length; i++) {
		order.push(i);
	}

	return order.sort( () => Math.random() - 0.5);
}

import { correlation_dependencies } from "../../helpers/correlation_dependencies.js"
export {print_curr_trial};

// position and font of counter message
const width = 800;
const x_cor_prac = '20%';
const x_cor_test = '0%';
const y_cor = '50%'; 
const font = '28px times';

// @param {object}   experiment
function print_curr_trial(experiment,type){
    let counter = d3.select('#counter')
                    .append('svg')
                    .attr('width',width)
                    .append('text');

    switch (type) {
        case 'jnd':
            if (!experiment.practice_end) {
                let cur_prac_condition_index = correlation_dependencies.get_subcondition_index(experiment, "practice");
                let total_practice = experiment.MAX_PRACTICE_TRIALS - 1;
                let number_practice_trial_complete = correlation_dependencies.get_adjusted_quantity_matrix(experiment,"practice")[cur_prac_condition_index].length;
                
                counter.attr("x", x_cor_prac).attr("y", y_cor).style("font", font)
                    .text('You have completed ' + number_practice_trial_complete + ' trials, ' + (total_practice - cur_prac_condition_index + 1) + ' remaining.');
            } else {
                let cur_sub_condition_index = correlation_dependencies.get_subcondition_index(experiment, "test");
                let total_sub_condi = experiment.sub_conditions_constants.length - 1;
                let num_trial_complete = correlation_dependencies.get_adjusted_quantity_matrix(experiment,"test")[cur_sub_condition_index].length;
                console.log()
                counter.attr("x", x_cor_test).attr("y", y_cor).style("font", font)
                    .text('You\'re now doing trial ' + (num_trial_complete) + ' under condition ' + (cur_sub_condition_index + 1) + ', ' + (total_sub_condi - cur_sub_condition_index ) + ' conditions remaining.');
            }
            break;
        case 'stevens':
            if(experiment.display_mode === "sample"){
                counter.attr("x", x_cor_test).attr("y", y_cor).style("font", font)
                .text(`Middle Correlation Value: ${experiment.sample_mid_correlation}.`, );
                return;
            }
            let block_type
            let total_sub_condi
            if(experiment.practice_end){
                block_type = 'test'
                total_sub_condi = experiment.sub_conditions_constants.length
            }
            else{
                block_type = 'practice'
                total_sub_condi = experiment.practice_conditions_constants.length
            }
            let cur_sub_condition_index = experiment.getIndex(block_type)
            let constants = experiment.getConstants(block_type,cur_sub_condition_index)
            let num_trial = experiment.num_trial_complete + 1 ;
            let num_conditions_remaining = total_sub_condi - cur_sub_condition_index;
            let trials_per_round = constants.trials_per_round
            counter.attr("x", x_cor_test).attr("y", y_cor).style("font", font)
                .text('You\'re now doing trial ' + (num_trial) + ' of ' + trials_per_round + ' under condition ' + (cur_sub_condition_index + 1) + ', ' + num_conditions_remaining + ' conditions remaining.');
    }
}

