/**
 * Generates a Custom Condition using the Custom Constants Data and the baseline experiment data (first of foundational)
 * Custom Constants Data overwrites Baseline Data if needed. Custom Condition Length is equal to Custom Data Length
 * @param {Object} experiment 
 */
function generate_custom_conditon_from_base_data(experiment){
	let condition_name = experiment.condition_name;
	let experiment_name = experiment.constructor.name;
	let custom_subconditions = EXPERIMENT_CONDITIONS[experiment_name][condition_name];
	let base_subcondition = EXPERIMENT_BASES[experiment_name]["custom"][0];
	let custom_condition  = []
	for(let i = 0; i < custom_subconditions.length; i++){
		let subcondition = Object.assign({}, base_subcondition, custom_subconditions[i]);
		custom_condition.push(subcondition);
	}
	console.log(custom_condition)
	return custom_condition;
}

/**
 * Generates subconditions for distractor_diamond_square conditions
 * Takes the "square" condition data from the constants and changes the dist_shape to diamond
 *
 * @param   {object}   experiment
 */
function generate_distractor_diamond_square_subconditions(experiment) {

	let array = experiment.condition_name.split("_");
	if (array.length !== 5) throw new Error(experiment.condition_name + " is not a valid distractor_diamond_square condition.");

	let square_name = array[0] + "_" + array[2] + "_" + array[3] + "_" + array[4];
	let experiment_name = experiment.constructor.name;
	let trial_structure = experiment.trial_structure;

	let subconditions = create_design_condition_dataset(EXPERIMENT_BASES[experiment_name][trial_structure], EXPERIMENT_CONDITIONS[experiment_name][square_name]);

	for (let c of subconditions) {
		c["dist_shape"] = "diamond";

		// Overwrite to opposing colors when dist_color = white
		if (c["dist_color"] === "WHITE"){
			// If RED
			if (c["target_color"] === "#cd4c32"){
				c["dist_color"] = "#007ebc";
			}
			// If BLUE
			else if (c["target_color"] === "#007ebc"){
				c["dist_color"] = "#cd4c32";
			}
			// If YELLOW
			else if (c["target_color"] === "#dbc667"){
				c["dist_color"] = "#20874a";
			}
			// If GREEN
			else {
				c["dist_color"] = "#dbc667";
			}
		}
	}

	return subconditions;
}

