/**
 * If X is a random variable with pdf f(x) and cdf F(X), then F(X) is a uniform(0, 1) distribution.
 * Takes in a normal point and passes it through the cdf function of a normal distribution
 * @param {float} x 
 * @returns {float}
 */
function normal_to_uniform(x){
  return 0.5*(1+erf(x/Math.sqrt(2)));
}

//https://stackoverflow.com/questions/1906064/gauss-error-function-implementation-for-javascript
function erf(x) {
  var z;
  const ERF_A = 0.147;
  var the_sign_of_x;
  if(0==x) {
    the_sign_of_x = 0;
    return 0;
  } else if(x>0){
    the_sign_of_x = 1;
  } else {
    the_sign_of_x = -1;
  }

  var one_plus_axsqrd = 1 + ERF_A * x * x;
  var four_ovr_pi_etc = 4/Math.PI + ERF_A * x * x;
  var ratio = four_ovr_pi_etc / one_plus_axsqrd;
  ratio *= x * -x;
  var expofun = Math.exp(ratio);
  var radical = Math.sqrt(1-expofun);
  z = radical * the_sign_of_x;
  return z;
}

