export function generate_color(default_color, hslo_color){
    if(hslo_color == null){
        return default_color;
    }
    if(hslo_color.length <3){
        throw Error("HSLO Object is too small. Requires at least HSL attributes");
    }
    let h = hslo_color[0];
    let s = hslo_color[1];
    let l = hslo_color[2];
    if(h < 0 || h > 360){
        throw Error ("Hue value out of bounds")
    }
    if(s < 0 || s > 1.0){
        throw Error ("Saturation value out of bounds")
    }
    if(l < 0 || l > 1.0){
        throw Error ("Luminosity value out of bounds")
    }
    let color = new d3.hsl(h,s,l)
    if(hslo_color.length == 4){
        let o = hslo_color[3]
        if(o < 0 || o > 1.0){
            throw Error ("Oppacity value out of bounds")
        }    
        color.opacity = o;
    }
    console.log(color)
    return color;
}

export function generate_color_hex(h, s, l, o = 1.0){
    check_hslo_ranges(h, s, l, o)

