    let hex_h = h.toString(16);
    let hex_s = s_255.toString(16);
    let hex_l = l_255.toString(16);
    let hex_o = o_255.toString(16);

    return hex_h + hex_s + hex_l + hex_o;
}

function check_hslo_ranges(h, s, l, o){
