          point_shape: ("target_shape" in trial_data ? trial_data.target_shape : "circle"),
          point_size:  ("point_size"   in trial_data ? trial_data.point_size   : 3),
          row: ("row" in trial_data? trial_data.row : 0),
          col: ("col" in trial_data? trial_data.col : 0),
          alternate_scaling: ("alternate_scaling" in trial_data ? trial_data.alternate_scaling : false),
          axis_length: ("axis_length" in trial_data ? trial_data.axis_length : null),
          mix_by_target_array: ("mix_by_target_array" in trial_data ? trial_data.mix_by_target_array : [])
        }
      },
      distractor: {
        dataset: distractors[i],
        graph_attributes: {
          point_color: ("dist_color" in trial_data ? trial_data.dist_color : "RED"),
          point_shape: ("dist_shape" in trial_data ? trial_data.dist_shape : "circle"),
          point_size:  ("point_size" in trial_data ? trial_data.point_size : 3),
          alternate_scaling: ("alternate_scaling" in trial_data ? trial_data.alternate_scaling : false),
          mix_by_distractor_array: ("mix_by_distractor_array" in trial_data ? trial_data.mix_by_distractor_array : [])
        }
      }
    };

    // Force pixels_per_cm property onto graph_attributes since dynamically generated
    attributes["target"]["graph_attributes"]["pixels_per_cm"] = experiment.PIXELS_PER_CM;
    
    console.log("create_distractor_scatter_plot")
    create_distractor_scatter_plot(attributes);

    // Set background color
    document.body.style.backgroundColor = (trial_data.background_color ? trial_data.background_color : "WHITE");
  }
}


/**
 * Retrieves attributes to run all estimation plots + calls correct plot method depending on 
 * condition.
 *
 * @param {object}   experiment
 */
function prepare_estimation_plot(experiment) {

  // Defer to model level for attribute computation
  let attributes = experiment.compute_plot_attributes();

  let name_array = experiment.condition_name.split("_");

  if (name_array.includes("interference")) {

    if (name_array.includes("multi")) {
      create_estimation_multi_interference_plot(experiment, attributes);
    }
    else {
      create_estimation_interference_plot(experiment, attributes);
    }

  }
  else if (name_array.includes("bisection")) {
    create_estimation_bisection_plot(experiment, attributes);
  }
  else {
    create_estimation_plot(experiment, attributes);
  }
}
/**
 * Prepares the attributes object for target and distractor datasets
 * to plot them with the combine_target_and_distractor function +
 * the specific plot function
 * @param {object} experiment 
 */
function prepare_distractor_plot(experiment){
  let datasets = experiment.coordinates;
  let distractors = experiment.distractor_coordinates;
  // console.log("distractor coords: " + JSON.stringify(distractors));
  let trial_data = experiment.trial_data;
  // console.log("trial data: " + JSON.stringify(trial_data));

  for (let i in datasets) {

    let attributes = {
      target: {
        dataset: datasets[i],
        graph_attributes: generate_attributes_object(experiment.graph_type, trial_data, i)
      },
      distractor: {
        dataset: distractors[i],
        graph_attributes: generate_attributes_object(experiment.graph_type, trial_data.dist_attributes, i)
      }
    };
    console.log("create_distractor_scatter_plot")
    // Only supports scatterplots for now
    create_scatter_plot(attributes.target, null, attributes.distractor);

    // Set background color
    document.body.style.backgroundColor = (trial_data.background_color ? trial_data.background_color : "WHITE");
  }

}

import { add_graph_features } from "../graph_feature_controller.js";

export {create_parallel_plot};

/**
 * D3 code for plotting a PCP.
 *
 * @param {object}   attributes
 */
function create_parallel_plot(attributes, style_attributes = null, distractor_attributes = null) {
	///console.log(style_attributes)
	let dataset = attributes.dataset;
    let properties = attributes.graph_attributes;
    const axis_separation = properties.axis_separation;
	const line_width = properties.line_width;
	const axis_width = properties.axis_width;

	let axis_scale = getAxisScale(properties);
	let x_data_scale = getXDataScale(properties);
	let y_data_scale = getYDataScale(properties);
	let height = getHeight(properties)

	// Create axes
	const x_axis = d3.axisLeft()
					.scale(axis_scale)
					.tickSize(0);

	const y_axis = d3.axisLeft()
					.scale(axis_scale)
					.tickSize(0);

	const chart = generate_chart(style_attributes, axis_separation, height, properties.buffer);

    // Creating transform SVG elements + append to SVG:
  	chart.append("g")
		.attr("transform", "translate(0, 0)")
		.attr("stroke", properties["axis_color"])
		.attr("stroke-width", `${axis_width}px`)
		.call(x_axis);

    chart.append("g")
		.attr("transform", `translate(${axis_separation}, 0)`)
		.attr("stroke", properties["axis_color"])
		.attr("stroke-width", `${3*axis_width}px`) // axis thickness is halved for some reason
		.call(y_axis)

	// Set axis color
	chart.selectAll("path")
	.attr("stroke", properties["axis_color"])

	// Adding graph features, if any, otherwise it calls plot_scatter_points normally
	add_graph_features(chart, attributes, distractor_attributes, x_data_scale, y_data_scale, plot_parallal_plot);

	//Remove tick labels
	chart.selectAll("text").remove();
}

/**
 * Plots the the dataset on a parallel plot
 * 
 * @param {*} chart 
 * @param {*} x_data_scale 
 * @param {*} y_data_scale 
 * @param {*} data 
 * @param {*} properties 
 */
function plot_parallal_plot(chart, x_data_scale, y_data_scale, data, properties){
	// Plot the Lines
	chart.selectAll("line_data")
			.data(data)
			.enter()
			.append("line")
			.style('stroke', properties["fill_color"])
			.style("stroke-width", function () {
				return properties.line_width;
			})
			.attr("x1", function (d){
				return 0;
			})
			.attr("y1", function (d){
				return x_data_scale(d[0]);
			})
			.attr("x2", function (d){
				return properties.axis_separation;
			})
			.attr("y2", function (d){
				return y_data_scale(d[1]);
			})
}

function generate_chart(style_attributes,axis_separation,height, buffer){
	let chart;
	if(style_attributes == null){
		chart = d3.select("#graph") // Insert into the div w/ id = "graph"
		.append("svg") 
		.attr("width", axis_separation) 
		.attr("height", height)
		.attr("style", `margin-left: ${buffer}; margin-right: ${buffer}; margin-top: 25vh;`);
	}
	else{
		chart = d3.select("#graph") // Insert into the div w/ id = "graph"
					.append("svg") 
					.attr("width", axis_separation) 
					.attr("height", height)
					.attr("style", `display: ${style_attributes.display} ; margin-left: ${style_attributes.left}; margin-right: ${style_attributes.right}; margin-top: ${style_attributes.top}; margin-bottom: ${style_attributes.bot}`);
	}
	return chart;
}

function getAxisScale(properties){
	let combined_length =  (properties.axis_height + 2*properties.axis_buffer);
	let axis_scale = d3.scaleLinear()
                   		.domain([0, 1]) 
                   		.range([0, combined_length]);
	return axis_scale;
}

function getXDataScale(properties){
	let axis_length = properties.axis_height
	let combined_length = (axis_length+properties.axis_buffer);
	let axis_scale = d3.scaleLinear()
                   		.domain([0, 1]) 
                   		.range([properties.axis_buffer, combined_length]);
	return axis_scale;
}

function getYDataScale(properties){
	let axis_scale;
	let axis_length = properties.axis_height
	let combined_length = (axis_length+properties.axis_buffer);
	if(properties.inverted){
		axis_scale = d3.scaleLinear()
						.domain([0, 1]) 
						.range([combined_length, properties.axis_buffer]);
	}
	else{
		axis_scale = d3.scaleLinear()
						.domain([0, 1]) 
						.range([properties.axis_buffer, combined_length]);
	
	}
	return axis_scale;
}

function getHeight(properties){
	let height;
	if(properties.axis_buffer){
		height = properties.axis_height + 2*properties.axis_buffer;
	}
	else{
		height = properties.axis_height;
	}
	return height
}

export {create_ring_plot};

/**
 * D3 code for plotting a ring plot.
 *
 * @param {object}   attributes
 */
function create_ring_plot(attributes) {

  let dataset = attributes["dataset"];
  let properties = attributes["graph_attributes"];

  let width = window.innerWidth * 0.8;
  let height = window.innerHeight * 0.3;

  // Scale for data slightly smaller than full width of axes to account for outliers.
  var xscale_for_data = d3.scaleLinear()
                 .domain([0, 1]) 
                 .range([window.innerWidth * 0.10, window.innerWidth * 0.70]);

  var xscale = d3.scaleLinear()
                 .domain([0, 1])
                 .range([0, width]);

  var yscale = d3.scaleLinear()
                 .domain([-1, 0])
                 .range([height/2, 0]);

  // Create axes: 
  var x_axis = d3.axisBottom()
                 .scale(xscale)
                 .tickSize([0]);

  var y_axis = d3.axisLeft()
                 .scale(yscale)
                 .tickSize([0]);
    
  var chart = d3.select("#graph") // Insert into the div w/ id = "graph"
                .append("svg") 
                  .attr("width", width) 
                  .attr("height", height)
                  .attr("transform", "scale(-1,1)"); // Flip horizontally so cone is
                                                     // is going left -> right (like orig. version)  

  var xAxisTranslate = height/2;
  var xAxisElements = chart.append("g")
                            .attr("transform", "translate(0, " + xAxisTranslate  +")")
                            .call(x_axis);

  // Populating data: 
  chart.selectAll("ring") // Technically no circles inside div yet, but will be creating it
        .data(dataset)
          .enter()
            .append("circle") // Creating the circles for each entry in data set 
            .attr("cx", function (d) { // d is a subarray of the dataset i.e coordinates [5, 20]
              return xscale_for_data(d[0]);
            })
            .attr("cy", function (d) {
              return height/2;
            })
            .attr("r", function (d) {
              return yscale(d[1]);
            })
            .attr("stroke", properties["stroke_color"])
            .attr("stroke-width", properties["ring_thickness"])
            .attr("fill", properties["fill_color"]);

  // Set axis color
  chart.selectAll("path")
       .attr("stroke", properties["axis_color"]);

  // Remove tick labels
  chart.selectAll("text").remove();     

}

