function create_distractor_scatter_plot(attributes) {

  let target_array_size = attributes["target"]["graph_attributes"]["mix_by_target_array"].length;
  let distractor_array_size = attributes["distractor"]["graph_attributes"]["mix_by_distractor_array"].length;
 
  if (target_array_size > 0 && distractor_array_size > 0) {
    // if there are mix attribute arrays (primarily for Feature Based Attention in Two Class Scatterplots experiment)
    // currently only works if both targets AND distractors have mix attribute arrays
    plot_mix_attribute_distractor_graphs(attributes)
  } else {

