  // Create axes: 
  let x_axis = d3.axisBottom()
    .scale(xscale)
    .tickSize([0]);

  let y_axis = d3.axisLeft()
    .scale(yscale)
    .tickSize([0]);

  // Append SVG into graph div
  let chart = d3.select("#graph")
    .append("svg")
    .attr("width", width)
    .attr("height", height*2/3)
    .attr("style", `margin-top: 25vh;`);

  // Creating transform SVG elements + append to SVG: 
  let yAxisElements = chart.append("g")
    .attr("transform", "translate(0, 0)")
    .call(y_axis);

  let xAxisTranslate = height / 2;

  let xAxisElements = chart.append("g")
    .attr("transform", "translate(0, " + xAxisTranslate + ")")
    .call(x_axis)

  let larger_data;
  let smaller_data;

  // Handle if the number of points between distractor & target are unequal
  if (attributes["target"]["dataset"].length > attributes["distractor"]["dataset"].length) {
    larger_data = attributes["target"];
    smaller_data = attributes["distractor"];
  } else {
    larger_data = attributes["distractor"];
    smaller_data = attributes["target"];
  }


// ____________________________________________________ TEST BLOCK ________________________________________________________________

  let NUM_LARGER_GROUPS, NUM_SMALLER_GROUPS, POINTS_PER_LARGER_GROUP, POINTS_PER_SMALLER_GROUP;

  /**
   * Distractor dataset will not have mix_by_target_array
   * Target dataset will not have mix_by_distractor_array
   */
  if (larger_data["graph_attributes"]["mix_by_target_array"] != null) {
    NUM_LARGER_GROUPS = larger_data["graph_attributes"]["mix_by_target_array"].length;
    NUM_SMALLER_GROUPS = smaller_data["graph_attributes"]["mix_by_distractor_array"].length;
    POINTS_PER_LARGER_GROUP = larger_data["dataset"].length / NUM_LARGER_GROUPS;
    POINTS_PER_SMALLER_GROUP = smaller_data["dataset"].length / NUM_SMALLER_GROUPS;
  } else {
    NUM_LARGER_GROUPS = larger_data["graph_attributes"]["mix_by_distractor_array"].length;
    NUM_SMALLER_GROUPS = smaller_data["graph_attributes"]["mix_by_target_array"].length;
    POINTS_PER_LARGER_GROUP = larger_data["dataset"].length / NUM_LARGER_GROUPS;
    POINTS_PER_SMALLER_GROUP = smaller_data["dataset"].length / NUM_SMALLER_GROUPS;
  }

/**
 * this loop has 3 primary functions:
 * 1. randomizes order of attribute arrays for both target and distractor populations
 * 2. overrides base properties of populations with properties contained within respective mix attribute arrays
 * 3. plots the points of each population one at a time, with equal chance of occluding one population over the other
 */
  for (let i = 0; i < NUM_LARGER_GROUPS; i++) {
    let larger_grouped_dataset = [];
    let smaller_grouped_dataset = [];

    //randomize larger dataset order
    for (let j = 0; j < POINTS_PER_LARGER_GROUP; j++) {

      //make sure dataset isn't empty
      if (larger_data["dataset"].length > 0) {
        let larger_rand_index = Math.floor(Math.random() * larger_data["dataset"].length);
        larger_grouped_dataset.push(larger_data["dataset"][larger_rand_index]);

        larger_data["dataset"].splice(larger_rand_index, 1);
      }
    }

    // randomize smaller dataset order
    for (let k = 0; k < POINTS_PER_SMALLER_GROUP; k++) {

      //make sure dataset isn't empty
      if (smaller_data["dataset"].length > 0) {
        let smaller_rand_index = Math.floor(Math.random() * smaller_data["dataset"].length);
        smaller_grouped_dataset.push(smaller_data["dataset"][smaller_rand_index]);

        smaller_data["dataset"].splice(smaller_rand_index, 1);
      }
    }

    // override base properties if present in mix arrays, for both larger & smaller datasets
    if (larger_data["graph_attributes"]["mix_by_target_array"] != null) {
      // case where larger_data is TARGET population

      for (var attrib in larger_data["graph_attributes"]["mix_by_target_array"][i]) {
        larger_data["graph_attributes"][attrib] = larger_data["graph_attributes"]["mix_by_target_array"][i][attrib];
      }

      for (var attrib in smaller_data["graph_attributes"]["mix_by_distractor_array"][i]) {
        smaller_data["graph_attributes"][attrib] = smaller_data["graph_attributes"]["mix_by_distractor_array"][i][attrib];
      }

    } else {
      // case where larger_data is DISTRACTOR population

      for (var attrib in smaller_data["graph_attributes"]["mix_by_target_array"][i]) {
        smaller_data["graph_attributes"][attrib] = smaller_data["graph_attributes"]["mix_by_target_array"][i][attrib];
      }

      for (var attrib in larger_data["graph_attributes"]["mix_by_distractor_array"][i]) {
        larger_data["graph_attributes"][attrib] = larger_data["graph_attributes"]["mix_by_distractor_array"][i][attrib];
      }
    }

    // alternate plotting of distractor and target dataset points. 
    // equal chance of one getting occluded over the other.
    for (let j in larger_grouped_dataset) {

      if (j < smaller_data["dataset"].length) {
        let small_point = smaller_grouped_dataset[j];
        plot_scatter_points(chart, xscale, yscale, [small_point], smaller_data["graph_attributes"]);
      }

      let large_point = larger_grouped_dataset[j];
      plot_scatter_points(chart, xscale, yscale, [large_point], larger_data["graph_attributes"]);
    }

    // Set axis color
    chart.selectAll("path")
      .attr("stroke", target_properties["axis_color"]);

    // Remove tick labels
    chart.selectAll("text").remove();
  }
}
