/**
 * Routes to correct plotting code depending on shape type.
 *
 * @param shape {string}
 * @param chart {object}
 * @param length {number} 
 * @param y_pos {number}
 * @param x_pos {number}
 * @param is_ref {boolean} if the shape is a reference shape or a modifiable shape
 * @param outline {string} outline color
 * @param fill {string} fill color
 * @param options {string} can be: {"scaling": "scales_with_mod"   This shape serves as bg to the modifiable shape
                                                OR 
                                               "scales_indep"      This shape is a bg shape, but is the one being modified
                                               },

                                   {"fan-attributes": {"slice-alignment"         : "top" or "bottom",   Position of slice in pie
                                                       "angle-size"              : some_value,          Angle in degrees of the fan
                                                      },          

                                   {"flicker": {"on": on_duration,    The duration in ms for the shape to appear
                                                "off": off_duration}} The duration in ms for the shape to disappear
 */
function plot_shape(shape, chart, length, y_pos, x_pos, is_ref, outline, fill, options) {
    switch (shape) {
        case "circle":
            plot_circle(chart, length, y_pos, x_pos, is_ref, outline, fill, options);
            break;
        case "fan":

            // When mod_side_alignment = slice-bottom, reference circle must have a sliced out
            // section equivalent to modifiable fan. Here we just plot another fan over the 
            // reference circle.
            if (options["fan-attributes"]["ref_shape_adjusts"]) {
                plot_fan(chart, length, y_pos-length/4, x_pos, is_ref, fill, fill, options);
            }

            plot_fan(chart, length, y_pos, x_pos, is_ref, outline, fill, options);

            break;
        case "triangle":
            plot_triangle(chart, length, y_pos, x_pos, is_ref, outline, fill, options);
            break;
        case "square":
            plot_square(chart, length, y_pos, x_pos, is_ref, outline, fill, options);
            break;
        case "line":
            plot_line(chart, length, y_pos, x_pos, is_ref, outline, options);
            break;
        case "rectangle":
            plot_rectangle(chart, length, y_pos, x_pos, is_ref, outline, fill, options);
            break;
        default:
            throw Error(shape + " shape is not supported.");
    }
}

/**
 * Plots a circle.
 *
 * @param chart {object}
 * @param diameter {number}
 * @param y_pos {number}
 * @param x_pos {number}
 * @param is_ref {boolean} if the shape is a reference shape or a modifiable shape
 * @param outline {string}
 * @param fill {string}
 * @param options {string}
 */
function plot_circle(chart, diameter, y_pos, x_pos, is_ref, outline, fill, options) {

    let radius = diameter / 2;
    let shape = chart.append("circle")
                        .attr("cx", x_pos)
                        .attr("cy", y_pos)
                        .attr("r", diameter / 2)
                        .attr("id", function(){
                            if (options && options.scaling && options.scaling === "scales_with_mod") {
                                exp.interf_shape_variables = {x_pos: x_pos, y_pos: y_pos, diameter: diameter};
                                return "bound_circle_shape"
                            }
                            return "circle_shape"}
                        )
                        .attr("is_ref", is_ref)
                        .attr("fill", fill)
                        .attr("stroke", outline);
    if (is_ref === false) {
        d3.select("body")
            .on("keydown", function () {
                let event = d3.event;
                // console.log(event);
                if (event.key === "m" || event.key === "z") {
                    diameter = calculate_size_change(event.key, diameter, "circle");
                    radius = diameter / 2;
                    d3.select("#circle_shape")
                        .attr("r", radius);
                }
            });
    }

    if (options && options.flicker) {
        shape.call(flicker_shape, fill, outline, options.flicker.on, options.flicker.off);
    }
}

/**
 * Plots a fan.
 *
 * @param chart {object}
 * @param width {number}
 * @param y_pos {number}
 * @param x_pos {number}
 * @param is_ref {boolean} if the shape is a reference shape or a modifiable shape,
 *                         is_ref === true if the shape is a reference shape
 * @param outline {string}
 * @param fill {string}
 * @param options {object}
 */
function plot_fan(chart, diameter, y_pos, x_pos, is_ref, outline, fill, options) {

    if (!options["fan-attributes"]) {throw Error("Fan attributes are needed in options to plot a fan.")};

    let radius = diameter/2;
    let angle_size = options["fan-attributes"]["angle_size"];

    let arc = d3.arc()
                .innerRadius(0)
                .outerRadius(radius)
                .startAngle(0 * Math.PI/180)
                .endAngle(function(){
                    return angle_size * Math.PI/180;
                });

    let shape = chart.append("g")
                     .attr("transform", function() {
                        return "translate(" + x_pos + "," + y_pos + ")";
                      }) 
                     .append("path")
                     .attr("id", function(){
                        if (is_ref) {
                            return "fan_shape_ref";
                        } else {
                            return "fan_shape_mod";
                        }
                      })
                     .attr("d", arc)
                     .attr("fill", fill)
                     .attr("stroke", outline)
                     .attr("stroke-width", 1)
                     .attr("transform", "rotate(" + compute_angle_shift(angle_size, options["fan-attributes"]["slice-alignment"]) + ")");
        
    if (is_ref === false) {
        d3.select("body")
          .on("keydown", function () {
                let event = d3.event;
                if (event.key === "m" || event.key === "z") {
                    // Estimated size = angle units
                    angle_size = calculate_angle_change(event.key, angle_size, radius);

                    let changed_arc = d3.arc()
                                        .innerRadius(0)
                                        .outerRadius(radius)
                                        .startAngle(0 * Math.PI/180)
                                        .endAngle(function(){
                                            return angle_size * Math.PI/180;
                                        });

                    d3.selectAll("#fan_shape_mod")
                        .attr("d", changed_arc)
                        .attr("transform", "rotate(" + compute_angle_shift(angle_size, options["fan-attributes"]["slice-alignment"]) + ")");
                }
        });
    }

    if (options && options.flicker) {
        shape.call(flicker_shape, fill, outline, options.flicker.on, options.flicker.off);
    }
}

/**
 * Returns the amount of rotational shift in degrees to align the angle 
 * at the desired alignment.
 *  
 * @param {double}   angle - size of angle in degrees
 *        {string}   alignment - e.g. bottom or top of circle
 */
function compute_angle_shift(angle, alignment) {

    let shift = (-1)*angle/2; // Shifts it so slice is perfectly centered and at top of pie

    if (alignment === "bottom") {
        shift += 180;
    } else if (alignment !== "top") {
        throw Error(alignment + " alignment not supported in computing angle shift for fan shapes.");
    }

    return shift;
}

/**
 * On conditions square,circle interference and circle interference, will trigger
 * the underlying bound bg shape to scale with the mod shape
 *  
 * @param {double}     size_change
 */
function adjust_interference_shape(size_change) {
    let subcond = exp.curr_conditions_constants[exp.curr_condition_index];
    let ratio = subcond.interf_ratio;

    let y_translation;
    let x_translation;

    if (subcond.interf_shape) {

        switch (subcond.interf_shape) {

            case "circle" :

                d3.select("#bound_circle_shape")
                  .attr("r", size_change * ratio)
                break;

            case "square" :

                x_translation = exp.interf_shape_variables.x_pos - ((size_change * ratio) / 2);
                y_translation = exp.interf_shape_variables.y_pos - ((size_change * ratio) / 2);

                d3.select("#bound_square_shape")
                  .attr("width", (size_change * ratio))
                  .attr("height", (size_change * ratio))
                  .attr("x", x_translation)
                  .attr("y", y_translation);
                break;
        }
    }
}

/**
 * Plots a square.
 *
 * @param chart {object}
 * @param width {number}
 * @param y_pos {number}
 * @param x_pos {number}
 * @param is_ref {boolean} if the shape is a reference shape or a modifiable shape,
 *                         is_ref === true if the shape is a reference shape
 * @param outline {string}
 * @param fill {string}
 * @param options {object}
 */
function plot_square(chart, width, y_pos, x_pos, is_ref, outline, fill, options) {

    let subcond = exp.curr_conditions_constants[exp.curr_condition_index];

    let shape;

    if (options && options.cutout_radius) {

        let cutout = options.cutout_radius;

        // Need to shift by half length to match centering done by regular rect svgs
        x_pos -= width/2;
        y_pos -= width/2;

        let poly = [{"x":(x_pos+cutout), "y":(y_pos)}, //bottom left of cutout
                    {"x":(x_pos+cutout), "y":(y_pos+cutout)}, //bottom right of cutout
                    {"x":(x_pos), "y":(y_pos+cutout)}, //top right of cutout

                    {"x":(x_pos), "y":(y_pos+width)}, //bottom left corner
                    {"x":(x_pos + width), "y":(y_pos + width)}, //bottom right corner
                    {"x":(x_pos + width), "y":(y_pos)}, //top right corner
                    ];

        shape = chart.append("polygon")
                        .attr("points",function() {
                            return poly.map(function(d) { return [d.x, d.y].join(","); }).join(" ");})
                        .attr("fill", fill)
                        .attr("stroke", outline)
                        .attr("id", is_ref? "square_cutout_shape_ref" : "square_cutout_shape_mod");

    } else {

        shape = chart.append("rect")
                        .attr("id", function(){
                            if (options && options.scaling && options.scaling === "scales_with_mod") {
                                exp.interf_shape_variables = {x_pos: x_pos, y_pos: y_pos, width: width};
                                return "bound_square_shape";
                            }

                            if (is_ref) {
                                return "square_shape_ref";
                            } else {
                                return "square_shape_mod"; // if is an interf and is not ref, becomes a mod
                            }
                        })
                        .attr("x", x_pos - width / 2)
                        .attr("y", y_pos - width / 2) // the x and y core_attributes for square
                                                      // refers to the position of the upper left corner
                                                      // however x_pos and y_pos specifies the center of the shape
                        .attr("width", width)
                        .attr("height", width)
                        .attr("fill", fill)
                        .attr("stroke", outline);

        if (is_ref === true && exp.curr_trial_data.ref_rotate_by) {
            let transform = "rotate(";
            transform = transform + exp.curr_trial_data.ref_rotate_by.toString();
            transform = transform + " " + (x_pos - width).toString();
            transform = transform + " " + (y_pos - width).toString();
            transform = transform + ")";
            d3.select("#square_shape_ref").attr("transform", transform);
        }
        if (is_ref === false) {
            d3.select("body")
                .on("keydown", () => {
                    let event = d3.event;
                    if (event.key === "m" || event.key === "z") {
                        width = calculate_size_change(event.key, width, "square");
                        d3.select("#square_shape_mod")
                            .attr("width", width)
                            .attr("height", width);

                        // Need to adjust translation for when adjustable
                        // interf is overlapping.
                        if (options && options.scaling && options.scaling === "scales_indep"){

                            let x_translation;
                            let y_translation;

                            // If interf is larger than ref
                            if (subcond.mod_ratio > 1) {
                                x_translation = x_pos - ((width * subcond.mod_ratio) / 4);
                                y_translation = y_pos - ((width * subcond.mod_ratio) / 4);
                            } else { // If interf is smaller than ref (so behind + center of ref)

                                x_translation = x_pos - ((width * subcond.mod_ratio));
                                y_translation = y_pos - ((width * subcond.mod_ratio));
                            }
                            
                            d3.select("#square_shape_mod")
                                .attr("x", x_translation)
                                .attr("y", y_translation);
                        }
                        
                    }
                });
        }
    }

    if (options && options.flicker) {
        shape.call(flicker_shape, fill, outline, options.flicker.on, options.flicker.off);
    }

}

/**
 * Plots a triangle. 
 *
 * @param chart {object}
 * @param radius {number}
 * @param y_pos {number}
 * @param x_pos {number}
 * @param is_ref {boolean} if the shape is a reference shape or a modifiable shape,
 *                         is_ref === true if the shape is a reference shape
 * @param outline {string}
 * @param fill {string}
 * @param options {object}
 */
function plot_triangle(chart, radius, y_pos, x_pos, is_ref, outline, fill, options) {

    // for equilateral triangles, height = side * sqrt(3) / 2;
    let short_side = radius;
    let long_side = radius;
    let height = 0, width = 0;

    let poly = [];
    if (!is_ref) {
        if (exp.curr_trial_data.width_height_ratio) {
            long_side = short_side * exp.curr_trial_data.width_height_ratio;
            height = Math.sqrt(Math.pow(long_side, 2) - Math.pow(short_side / 2, 2));
            width = short_side;
            if (exp.curr_trial_data.mod_rotate_by) {
                poly = [
                    {"x":(0.5 * height + x_pos), "y":(y_pos)},
                    {"x":(-0.5 * height + x_pos), "y":(-0.5 * width + y_pos)},
                    {"x":(-0.5 * height + x_pos), "y":(0.5 * width + y_pos)}];
            } else {
                poly = [
                    {"x":(x_pos), "y":(-0.5 * height + y_pos)},
                    {"x":(-0.5 * width + x_pos), "y":(0.5 * height + y_pos)},
                    {"x":(0.5 * width + x_pos), "y":(0.5 * height + y_pos)}];
            }
        } else {
            height = radius * Math.sqrt(3)/2;
            poly = [
                {"x":x_pos, "y":(-0.5 * height + y_pos)},
                {"x":(-0.5 * radius + x_pos), "y":(0.5 * height + y_pos)},
                {"x":(0.5 * radius + x_pos), "y":(0.5 * height + y_pos)}];
        }
    } else {
        height = radius * Math.sqrt(3)/2;
        poly = [
            {"x":x_pos, "y":(-0.5 * height + y_pos)},
            {"x":(-0.5 * radius + x_pos), "y":(0.5 * height + y_pos)},
            {"x":(0.5 * radius + x_pos), "y":(0.5 * height + y_pos)}];
    }

    let shape = chart.append("polygon")
                        .attr("points",function() {
                            return poly.map(function(d) { return [d.x, d.y].join(","); }).join(" ");})
                        .attr("fill", fill)
                        .attr("stroke", outline)
                        .attr("id", is_ref? "triangle_shape_ref" : "triangle_shape_mod");

    if (is_ref === false) {
        d3.select("body")
            .on("keydown", function () {
                let event = d3.event;
                if (event.key === "m" || event.key === "z") {
                    // decide the amount of change;
                    radius = calculate_size_change(event.key, radius, "triangle");
                    // plot the changed shape
                    short_side = radius;
                    if (exp.curr_trial_data.width_height_ratio) {
                        long_side = short_side * exp.curr_trial_data.width_height_ratio;
                        height = Math.sqrt(Math.pow(long_side, 2) - Math.pow(short_side / 2, 2));
                        width = short_side;
                        if (exp.curr_trial_data.mod_rotate_by) {
                            poly = [
                                {"x":(0.5 * height + x_pos), "y":(y_pos)},
                                {"x":(-0.5 * height + x_pos), "y":(-0.5 * width + y_pos)},
                                {"x":(-0.5 * height + x_pos), "y":(0.5 * width + y_pos)}];
                        } else {
                            poly = [
                                {"x":(x_pos), "y":(-0.5 * height + y_pos)},
                                {"x":(-0.5 * width + x_pos), "y":(0.5 * height + y_pos)},
                                {"x":(0.5 * width + x_pos), "y":(0.5 * height + y_pos)}];
                        }
                    } else {
                        height = short_side * Math.sqrt(3)/2;
                        poly = [
                            {"x":x_pos, "y":(-0.5 * height + y_pos)},
                            {"x":(-0.5 * short_side + x_pos), "y":(0.5 * height + y_pos)},
                            {"x":(0.5 * short_side + x_pos), "y":(0.5 * height + y_pos)}];
                    }

                   chart.select("#triangle_shape_mod")
                        .attr("points",function() {
                            return poly.map(function(d) { return [d.x, d.y].join(","); }).join(" ");});

                   adjust_interference_shape(radius);

                }
            });
    }

    if (options && options.flicker) {
        shape.call(flicker_shape, fill, outline, options.flicker.on, options.flicker.off);
    }
}

/**
 * Plots a rectangle.
 *
 * @param chart {object}
 * @param size {number}
 * @param y_pos {number}
 * @param x_pos {number}
 * @param is_ref {boolean}
 * @param outline {string}
 * @param fill {string}
 * @param options {object}
 */
function plot_rectangle(chart, size, y_pos, x_pos, is_ref, outline, fill, options) {
    let short_side = size;
    let long_side = size;
    let height = 0, width = 0;
    if (exp.curr_trial_data.width_height_ratio) {
        long_side = short_side * exp.curr_trial_data.width_height_ratio;
    }
    width = short_side;
    height = long_side;
    let shape = chart.append("rect")
                    .attr("id", is_ref? "rect_shape_ref": "rect_shape_mod")
                    .attr("x", x_pos - width / 2)
                    .attr("y", y_pos - height / 2) // the x and y core_attributes for square
                    // refers to the position of the upper left corner
                    // however x_pos and y_pos specifies the center of the shape
                    .attr("width", width)
                    .attr("height", height)
                    .attr("fill", fill)
        .attr("stroke", outline);
    if (is_ref === false && exp.curr_trial_data.mod_rotate_by) {
        let transform = "rotate(";
        transform = transform + exp.curr_trial_data.mod_rotate_by.toString();
        transform = transform + " " + (x_pos).toString();
        transform = transform + " " + (y_pos).toString();
        transform = transform + ")";
        console.log(transform);
        d3.select("#rect_shape_mod").attr("transform", transform);
    }

    if (is_ref === false) {
        d3.select("body")
            .on("keydown", function () {
                let event = d3.event;
                if (event.key === "m" || event.key === "z") {
                    size = calculate_size_change(event.key, size, "rectangle");
                    let short_side = size;
                    let long_side = exp.curr_trial_data.width_height_ratio * short_side;
                    let new_width = 0, new_height = 0;
                    new_width = short_side;
                    new_height = long_side;
                    d3.select("#rect_shape_mod")
                        .attr("width", new_width)
                        .attr("height", new_height);
                }
            });
    }

    if (options && options.flicker) {
        shape.call(flicker_shape, fill, outline, options.flicker.on, options.flicker.off);
    }

}

/**
 * Plots a line.
 *
 * @param chart {object}
 * @param width {number}
 * @param y_pos {number}
 * @param x_pos {number}
 * @param is_ref {boolean}
 * @param outline
 * @param options {object}
 */
function plot_line(chart, width, y_pos, x_pos, is_ref, outline, options) {

    let x1, x2, y1, y2;
    if (!is_ref) {
        x1 = x_pos - (width / 2) * Math.sin(exp.curr_trial_data.mod_rotate_by * Math.PI / 180);
        x2 = x_pos + (width / 2) * Math.sin(exp.curr_trial_data.mod_rotate_by * Math.PI / 180);
        y1 = y_pos - (width / 2) * Math.cos(exp.curr_trial_data.mod_rotate_by * Math.PI / 180);
        y2 = y_pos + (width / 2) * Math.cos(exp.curr_trial_data.mod_rotate_by * Math.PI / 180);
    } else {
        x1 = x_pos - width / 2;
        x2 = x_pos + width / 2;
        y1 = y_pos;
        y2 = y_pos;
    }
    let shape = chart.append("line")
                        .style("stroke", outline)
                        .style("stroke-width", exp.curr_trial_data.stroke_width)
                        .attr("id", is_ref? "line_shape_ref": "line_shape_mod")
                        .attr("x1", x1)
                        .attr("x2", x2)
                        .attr("y1", y1)
                        .attr("y2", y2);
    if (is_ref === false) {
        d3.select("body")
            .on("keydown", function () {
                let event = d3.event;
                if (event.key === "m" || event.key === "z") {
                    width = calculate_size_change(event.key, width, "line");
                    if (!is_ref) {
                        x1 = x_pos - (width / 2) * Math.sin(exp.curr_trial_data.mod_rotate_by * Math.PI / 180);
                        x2 = x_pos + (width / 2) * Math.sin(exp.curr_trial_data.mod_rotate_by * Math.PI / 180);
                        y1 = y_pos - (width / 2) * Math.cos(exp.curr_trial_data.mod_rotate_by * Math.PI / 180);
                        y2 = y_pos + (width / 2) * Math.cos(exp.curr_trial_data.mod_rotate_by * Math.PI / 180);
                    } else {
                        x1 = x_pos - width / 2;
                        x2 = x_pos + width / 2;
                        y1 = y_pos;
                        y2 = y_pos;
                    }
                    d3.select("#line_shape_mod")
                        .attr("x1", x1)
                        .attr("x2", x2)
                        .attr("y1", y1)
                        .attr("y2", y2);
                }
            });
    }

    if (options && options.flicker) {
        shape.call(flicker_shape, "none", outline, options.flicker.on, options.flicker.off);
    }
}

/**
 * Plots text centered at the bottom of the page. 
 *
 * @param chart {object}
 *        text  {string}
 */
function plot_text(chart, text){

    chart.append("text")
        .attr("x", "50%")
        .attr("y", "95%")
        .attr("text-anchor", "middle")
        .attr("font-family", "sans-serif")
        .attr("font-size", "28px")
        .attr("fill", "black")
        .text(()=>{
            return text;
        });
}

/**
 * Flickers on and off the D3 selection.
 *
 * @param {object} selection
 *        {string} fill
 *        {string} stroke
 *        {double} on_duration - duration selection displays for in ms
 *        {double} off_duration - duration selection becomes invisible for in ms
 */
function flicker_shape(selection, fill, stroke, on_duration, off_duration) {

    setInterval(display_off, on_duration);
    setInterval(display_on, on_duration + off_duration);

    function display_off() {
        selection.attr("fill", "none")
                 .attr("stroke", "WHITE");
    }

    function display_on() {
        selection.attr("fill", fill)
                 .attr("stroke", stroke);
    }
}

/**
 * Calculates the next size and saves the data.
 *
 * @param {object}  event_key  m to increase the size and z to decrease the size
 * @param {double}  size the previous size of the shape
 * @param {string}  the type of shape
 *
 * @returns {number}  the new size in pixels
 */
function calculate_size_change(event_key, size, shape_type) {

    let sign = event_key === "m" ? 1 : -1;
    let change = Math.random() * exp.PIXEL_TO_CM * exp.MAX_STEP_SIZE;
    let new_radius = size + sign * change;
    let size_in_px = new_radius;
    let size_in_cm = new_radius / exp.PIXEL_TO_CM;

    exp.save_adjustment(change * sign / exp.PIXEL_TO_CM);
    exp.save_estimated_size(size_in_cm, "cm");

    let area = exp.compute_shape_area(shape_type, size_in_px);
    exp.save_estimated_area(area);

    return size_in_px;
}

/**
 * Calculates the next angle and saves the data.
 *
 * @param {object}  event_key  m to increase the size and z to decrease the size
 * @param {double}  previous angle
 * @param {double}  radius of the fan
 *
 * @returns {number}  the new angle in degrees
 */
function calculate_angle_change(event_key, angle, radius) {

    let current_constants = exp.curr_conditions_constants[exp.curr_condition_index];
    let max_step_size = current_constants.max_step_size;

    let sign = event_key === "m" ? 1 : -1;
    let change = Math.random() * max_step_size;
    let diff_angle = sign * change;

    let new_angle = angle + diff_angle;

    exp.save_adjustment(diff_angle);
    exp.save_estimated_size(new_angle, "angle");

    let area = exp.compute_fan_area(new_angle, radius);
    exp.save_estimated_area(area);

    return new_angle;
}

/**
 * Alternates the plotting of target and distractor graph points so they are equally mixed, otherwise
 * one type of graph will be in the foreground and the other in the background depending on which one
 * is plotted first.
 * 
 * @param {assoc array} target - target dataset and d3 attributes
 * @param {assoc array} distractor - distractor dataset and d3 attributes
 * @param {object} chart - d3 chart
 * @param {function} xscale - x value scale function
 * @param {function} yscale - y value scale function
 * @param {function} plot_graph - (chart, xscale, yscale, data, properties) d3 graph plotting function 
 * @param {function} plot_mix_graph - (chart, xscale, yscale, large_points, graph_attributes, plot_graph)
 */
export function combine_target_and_distractor(target, distractor, chart, xscale, yscale, plot_graph, plot_mix_graph = null){
  let larger_data;
  let smaller_data;
  
  // Handle if the number of points between distractor & target are unequal
  if (target["dataset"].length >= distractor["dataset"].length) {
    larger_data = target;
    smaller_data = distractor;
  } else {
    larger_data = distractor;
    smaller_data = target;
  }
 
  // Get the number of groups and points per group in target and distractor
  // Attributes without mix_data have 1 group in them (the total set)
  let NUM_LARGER_GROUPS = get_number_of_data_groups(larger_data["graph_attributes"]["mix_data"]);
  let NUM_SMALLER_GROUPS = get_number_of_data_groups(smaller_data["graph_attributes"]["mix_data"]);
  let LARGER_GROUP_NUMBER = math.max(NUM_LARGER_GROUPS,NUM_SMALLER_GROUPS);

  let POINTS_PER_LARGER_GROUP = larger_data["dataset"].length / NUM_LARGER_GROUPS;
  let POINTS_PER_SMALLER_GROUP = smaller_data["dataset"].length / NUM_SMALLER_GROUPS;
  let surplus_large_points = larger_data["dataset"].length - smaller_data["dataset"].length;

  if((surplus_large_points % NUM_LARGER_GROUPS && larger_data["graph_attributes"]["mix_type"] === "attribute")){
    throw new Error(" Incompatible Sets of Point sizes for dual Mixed Attribute plotting. " +
    `The surplus number of points is ${surplus_large_points} and it should be a multiple of ${NUM_LARGER_GROUPS}.`)
  }

  // Plotting surplus large points first
  // Surplus points should be a valid number for plot_mix_graph
  let large_points = larger_data["dataset"].slice(0, surplus_large_points);
  plot_points(chart, xscale, yscale, large_points, larger_data["graph_attributes"], plot_graph , plot_mix_graph);
  let plotted_large_points = surplus_large_points;

  // Plotting remaining points in mix_data groups
  let plotted_small_points = 0;
  for(let j = 0; j < POINTS_PER_SMALLER_GROUP; j++){
    let start_index = j * LARGER_GROUP_NUMBER;
    let large_points = larger_data["dataset"].slice(start_index, start_index + LARGER_GROUP_NUMBER);
    plot_points(chart, xscale, yscale, large_points, larger_data["graph_attributes"], plot_graph , plot_mix_graph);
    let small_points = smaller_data["dataset"].slice(start_index, start_index + LARGER_GROUP_NUMBER);
    plot_points(chart, xscale, yscale, small_points, smaller_data["graph_attributes"], plot_graph, plot_mix_graph)
    plotted_small_points += LARGER_GROUP_NUMBER;
    plotted_large_points += LARGER_GROUP_NUMBER;
    if(plotted_small_points >= smaller_data["dataset"].length){
      break;
    }
  }
 
}

/**
 * Gets the number of data groups in the mix_data.
 * If it is not a mix condition, there is only 1 group which is the
 * default group
 * 
 * @param {object} mix_data 
 * @returns {number}
 */
function get_number_of_data_groups(mix_data){
  if(mix_data === null) return 1;
  let count = 0;
  for(let key in mix_data){    
    count += mix_data[key].length;
  }
  return count;
}

/**
 * Plots the given points, using the plot_mix_graph function if appropiate
 * or the given plot_graph function.
 * 
 * @param {*} chart 
 * @param {*} xscale 
 * @param {*} yscale 
 * @param {*} points 
 * @param {*} graph_attributes 
 * @param {*} plot_graph 
 * @param {*} plot_mix_graph 
 */
function plot_points(chart, xscale, yscale, points, graph_attributes , plot_graph , plot_mix_graph){
  if(graph_attributes["mix_data"] != null){
    plot_mix_graph(chart, xscale, yscale, points, graph_attributes, plot_graph)
  } else{
    plot_graph(chart, xscale, yscale, points, graph_attributes);
  }
}

/**
 * Plots the given plot_graph function using the given mix_type
 * @param {object} chart d3 chart reference
 * @param {function} x_data_scale data scaling function for the x values 
 * @param {function} y_data_scale  data scaling function for the y values
 * @param {number[number[]]} dataset {number[x_value, y_value]{}
 * @param {object} properties graph properties
 * @param {function} plot_graph (chart, x_data_scale, y_data_scale, dataset, properties)
 *  
 */
export function plot_mixed_attribute_graph(chart, x_data_scale, y_data_scale, dataset, properties, plot_graph){
  switch(properties.mix_type){
    case "none":
      plot_graph(chart, x_data_scale, y_data_scale, dataset, properties);
      //throw Error(`plot_mixed_attribute_graph called for "none" mix_type.`)
      break;
    case "attribute":
      prepare_mix_graph(chart, x_data_scale, y_data_scale, dataset, properties, plot_graph);
      break;
    case "targeted":
      prepare_targeted_mix_graph(chart, x_data_scale, y_data_scale, dataset, properties, plot_graph);
      break;
    case "attribute_array":
      prepare_array_mix_graph(chart, x_data_scale, y_data_scale, dataset, properties, plot_graph);
      break;
    default: 
      throw Error(`mix_type ${properties.mix_type} has not been handled.`)
  }
}

/**
 * Will plot points depending on what is defined as the mix attribute,
 * and will sample (w/o replacement) from the dataset to display points
 * with all defined values for the mix attribute.
 *
 * E.g. if properties["mix_data"] = {"point_size" = [1, 2, 3, 4]}
 *      and there are 100 points in the dataset, will plot 25 points with
 *      point_size = 1, 25 points with point_size = 2, etc...
 * 
 * E.g. if properties["mix_data"] = {"point_size" = [1, 2], "point_color" = [black, blue]}
 *      and there are 100 points in the dataset, will first convert the set into the array 
 *      [{"point_size" = 1, "point_color" = red}, {"point_size" = 2, "point_color" = red},
 *      {"point_size" = 1, "point_color" = blue}, {"point_size" = 2, "point_color" = blue}], 
 *      then plot 25 points with point_size = 1 and point_color = red, 
 *      25 points with point_size = 2 and point_color = red, etc...
 *
 * E.g. if properties["mix_data"] = {"point_size" = [1, 2], "point_color" = [black, blue]}
 *      and there are 100 points in the dataset, will first convert the set into the array 
 *      [{"point_size" = 1, "point_color" = red}, {"point_size" = 2, "point_color" = red},
 *      {"point_size" = 1, "point_color" = blue}, {"point_size" = 2, "point_color" = blue}], 
 *      then plot 25 points with point_size = 1 and point_color = red, 
 *      25 points with point_size = 2 and point_color = red, etc...
 *
 * @param {object}         chart
 * @param {function}       xscale
 * @param {function}       yscale
 * @param {number[]}          dataset ([x_value, y_value])
 * @param {assoc. array}   e.g. {"point_shape": "square", "point_size": 5 .... }
 */
export function prepare_mix_graph(chart, xscale, yscale, dataset, properties, plot_graph) {

    let NUM_GROUPS = 1;
    let mix_attribs = []
    let mix_attribs_pairs = [{}];
  
    for (let x = 0; x < Object.keys(properties["mix_data"]).length; x++){
      let mix_attrib = Object.keys(properties["mix_data"])[x];
      mix_attribs.push(mix_attrib);
      NUM_GROUPS = NUM_GROUPS * properties["mix_data"][mix_attrib].length;
  
      let new_mix_attribs = [];
      properties["mix_data"][mix_attrib].forEach(e => {
  
        let temp_mix_attribs = [];
        mix_attribs_pairs.forEach(original => {
          var copy = {};
          for(var key in original)
            {
                copy[key] = original[key];
            }
          temp_mix_attribs.push(copy)
        });
  
        temp_mix_attribs.forEach(element => {
          element[mix_attrib] = e;
        });
        new_mix_attribs = new_mix_attribs.concat(temp_mix_attribs);  
      });
      mix_attribs_pairs = new_mix_attribs;
    }
  
    let POINTS_PER_GROUP = dataset.length / NUM_GROUPS;
  
    if(dataset.length % NUM_GROUPS > 0){
      throw Error(
      `ERROR: DATASET CAN'T BE DEVIDED BY NUM_GROUPS EVENLY.
      dataset.length: ${dataset.length}
      NUM_GROUPS: ${NUM_GROUPS}
      POINTS_PER_GROUP: ${POINTS_PER_GROUP}
      dataset.length % NUM_GROUPS = ${(dataset.length % NUM_GROUPS)}` 
      )
    }
  
  
    for (let i = 0; i < NUM_GROUPS; i++) {
  
      let grouped_dataset = [];
  
      for (let j = 0; j < POINTS_PER_GROUP; j++) {
  
        let rand_index = Math.floor(Math.random() * dataset.length);
        grouped_dataset.push(dataset[rand_index]);
  
        // Remove from dataset
        dataset.splice(rand_index, 1);
      }
  
      // Override the property if it is a mix attribute
    
      for (let y = 0; y < mix_attribs.length; y++) {
        properties[mix_attribs[y]] = mix_attribs_pairs[i][mix_attribs[y]];         
      }
      // Plot this group of points with the ith value for the mix attribute
      plot_graph(chart, xscale, yscale, grouped_dataset, properties);
    }
  }
  
/**
 * The same as prepare_mix_graph but now the first item in the mix by attributes array
 * is treated as the target
 * The user can specify how many target points there are
 * The rest of the mix by attributes are treated as distractors
 * and the remaining number of points is distributed evenly among them
 * 
 * e.g. if if properties["mix_by_attribute_targeted"] = {"point_size" = [1, 2, 3, 4], num_target: 10}
 *      and there are 100 points in the dataset, will plot 10 points with
 *      point_size = 1, 30 points with point_size = 2, 3 and 4.
 *
 * @param {object}         chart
 * @param {function}       xscale
 * @param {function}       yscale
 * @param {number[]}       dataset ([x_value, y_value])
 * @param {assoc. array}   e.g. {"point_shape": "square", "point_size": 5 .... }
 */
export function prepare_targeted_mix_graph(chart, xscale, yscale, dataset, properties, plot_graph) {
  let mix_attrib = Object.keys(properties["mix_data"])[0];
  let POINTS_FOR_TARGET = properties["mix_data"]["num_target"];
  
  let NUM_GROUPS = properties["mix_data"][mix_attrib].length;
  if (POINTS_FOR_TARGET < 0) {
    NUM_GROUPS = NUM_GROUPS - 1;
  }
  let POINTS_PER_GROUP = (dataset.length - POINTS_FOR_TARGET) / NUM_GROUPS;

  for (let i = 0; i < NUM_GROUPS; i++) {

    let grouped_dataset = [];
    if (i == 0 && POINTS_FOR_TARGET == 0) {
      continue;
    }
  
    if (i == 0) {
      for (let h = 0; h < POINTS_FOR_TARGET; h++) {
        let rand_index = Math.floor(Math.random() * dataset.length);
        grouped_dataset.push(dataset[rand_index]);
        // Remove from dataset
        dataset.splice(rand_index, 1);
      }
    } else {
        for (let j = 0; j < POINTS_PER_GROUP; j++) {
          let rand_index = Math.floor(Math.random() * dataset.length);
          grouped_dataset.push(dataset[rand_index]);

          // Remove from dataset
          dataset.splice(rand_index, 1);
        }
    }

    // Override the property if it is a mix attribute
    properties[mix_attrib] = properties["mix_data"][mix_attrib][i];
    // Plot this group of points with the ith value for the mix attribute
    plot_graph(chart, xscale, yscale, grouped_dataset, properties);
  }

}

/**
 * similar to mix_by_attribute, prepare_array_mix_graph will plot points
 * depending on what is defined as the mix attributes. However, mix_by_attribute_array
 * allows for several attributes to be changed within a single graph.
 * 
 * e.g. if properties["mix_by_attribute_array"] =
 *      [{"point_size" = 1}, 
 *      {"point_size" = 2, "point_color" = blue, "point_shape" = circle}, 
 *      {"point_size" = 3, "point_color" = red},
 *      {"point_size" = 4, "point_color" = red},]
 *      and there are 100 points in the dataset, will plot 25 points with
 *      point_size = 1, 25 points with point_size = 2 and point_color = blue and point_shape = circle, etc...
 * 
 * 
 * @param {*} chart 
 * @param {*} xscale 
 * @param {*} yscale 
 * @param {*} dataset ([x_value, y_value])
 * @param {*} properties {"point_shape": "square", "point_size": 5 .... }
 */
export function prepare_array_mix_graph(chart, xscale, yscale, dataset, properties, plot_graph) {
  let NUM_GROUPS = properties["mix_data"].length;
  let mix_attrib_array = properties["mix_data"];
  let POINTS_PER_GROUP = dataset.length / NUM_GROUPS;

  for (let i = 0; i < NUM_GROUPS; i++) {

    let grouped_dataset = [];

    for (let j = 0; j < POINTS_PER_GROUP; j++) {
      //make sure the dataset is not empty
      if(dataset.length > 0){
        let rand_index = Math.floor(Math.random() * dataset.length);
        grouped_dataset.push(dataset[rand_index]);

        // Remove from dataset
        dataset.splice(rand_index, 1);
      }
    }

    // Override the property if it is a mix attribute
    for(var attrib in mix_attrib_array[i]){
      // console.log("attrib")
      // console.log(attrib);
      properties[attrib] = mix_attrib_array[i][attrib];   
    }    

    // console.log("properties")
    // console.log(properties);

    // Plot this group of points with the ith value for the mix attribute
    plot_graph(chart, xscale, yscale, grouped_dataset, properties);
  }
}

/**
 * D3 code for appending data to the graph depending on point shape type.
 *
 * @param {object}         chart
 * @param {function}       xscale
 * @param {function}       yscale
 * @param {array}          data ([x_value, y_value])
 * @param {assoc. array}   properties e.g. {"point_shape": "square", "point_size": 5 .... }
 */
export function prepare_visual_mask_graph(chart, properties) {
    const width = properties["chart_width"];
    const height = properties["chart_height"];
    let rect_width = properties["chart_width"]*properties["visual_masking_ratio"];
    let rect_height = properties["chart_height"];
    switch(properties["visual_masking_pos"]){
        case "middle":
            chart.append("svg")
                .append("rect")
                .attr("class","mask")
                .attr("x",width/2-rect_width/2)
                .attr("y",width/2-rect_height/2)
                .attr("width",rect_width)
                .attr("height",rect_height)
                .attr("fill","white")
                .attr("transform",`rotate(-45,${width/2},${width/2})`);
            break;
        case "sides":
            rect_width = rect_width/2
            // rectangle at right side
            chart.append("svg")
                .append("rect")
                .attr("class","mask")
                .attr("x",width-rect_width)
                .attr("y",width/2-rect_height/2)
                .attr("width",rect_width)
                .attr("height",rect_height)
                .attr("fill","white")
                .attr("transform",`rotate(-45,${width/2},${width/2})`);
                // rectangle at left side
            chart.append("svg")
                .append("rect")
                .attr("class","mask")
                .attr("x",0)
                .attr("y",width/2-rect_height/2)
                .attr("width",rect_width)
                .attr("height",rect_height)
                .attr("fill","white")
                .attr("transform",`rotate(-45,${width/2},${width/2})`);
            break;
        case "fence":
            let num_mask = 5;
            rect_width = rect_width/num_mask;
            for (let i = 0;i<num_mask;i++){
              chart.append("svg")
                .append("rect")
                .attr("class","mask")
                .attr("x",i*rect_width/properties["visual_masking_ratio"])
                .attr("y",width/2-rect_height/2)
                .attr("width",rect_width)
                .attr("height",rect_height)
                .attr("fill","white")
                .attr("transform",`rotate(-45,${width/2},${width/2})`);
            }
            break;
    } 
    switch(properties["visual_masking_type"]){
        case "border":
            d3.selectAll(".mask")
            .attr("stroke","black");
            
            // delete (cover with white) addtional mask segment beyond the axis
            chart.append("svg")
            .append("rect")
            .attr("x",0)
            .attr("y",width)
            .attr("width",width)
            .attr("height",height)
            .attr("fill","white")
            break;
    }
  }

import {create_scatter_plot} from "/scripts/experiment-properties/graphing/d3-base-plots/scatter_plot.js";
import {create_strip_plot} from "/scripts/experiment-properties/graphing/d3-base-plots/strip_plot.js";
import {create_ring_plot} from "/scripts/experiment-properties/graphing/d3-base-plots/ring_plot.js";
import {create_shape_plot} from "/scripts/experiment-properties/graphing/d3-base-plots/shape_plot.js";
import {create_parallel_plot} from "/scripts/experiment-properties/graphing/d3-base-plots/parallel_plot.js";
import {is_custom_plot, prepare_custom_plot} from "/scripts/experiment-properties/graphing/custom_graphing_controller.js";
import { GRAPH_TYPES, GRAPH_FEATURES } from "/config/graphing-config.js";

export {plot_distributions};

/**
 * Main point of entry to determine whether there is a custom plotting code for
 * the condition, or we use one of the standard supported graph plots.
 *
 * @param {object}   experiment
 */
function plot_distributions(experiment) {
	// If experiment uses a custom plotting code not supported by
	// main plots:
	if (is_custom_plot(experiment)){
		return prepare_custom_plot(experiment);
	}

	switch (experiment.graph_type) {
		case "scatter":
			prepare_scatter_plot(experiment);
			break;

		case "strip":
			prepare_strip_plot(experiment);
			break;

		case "ring":
			prepare_ring_plot(experiment);
			break;

		case "shapes":
			prepare_shapes_plot(experiment);
			break;
		
		case "parallel":
			prepare_parallel_plot(experiment);
			break;
		case "mixed":
			prepare_mixed_plot(experiment);
			break;	
		default:
			throw Error("Graph type " + experiment.graph_type + " is not supported.");
	}
}

/**
 * Sets up attributes from trial data for scatter plots and creates the plots.
 *
 * @param {object}   experiment
 */
function prepare_scatter_plot(experiment) {
	
	let datasets = experiment.coordinates;
	let trial_data = experiment.trial_data;

    for (let i in datasets) {
		let attributes = generate_graphing_attributes(experiment, trial_data, datasets, i);
		console.log(attributes)
		let style_attributes = generate_style_attributes(trial_data, i);
		let distractor_attribtues = generate_distractor_attributes(experiment, trial_data, i);
		// Force specific numerosity attributes onto graph_attributes if doing numerosity experiment
		attributes.graph_attributes["alternate_scaling"] = trial_data.alternate_scaling;
		attributes.graph_attributes["row"] = trial_data.row;
		attributes.graph_attributes["col"] = trial_data.col;
	    create_scatter_plot(attributes, style_attributes, distractor_attribtues);
	}

    // Set background color
