function prepare_mixed_plot(experiment) {
	let datasets = experiment.coordinates; 
	let graph_properties = experiment.graph_properties;
	let trial_data = experiment.trial_data;
    for (let i in datasets) {
		let graph_type = trial_data[graph_properties[i]]["graph_type"];
		let graph_data = trial_data[graph_properties[i]];
		//let attributes = generate_graphing_attributes(experiment, trial_data, i)
		let dataset = datasets[i];
		let graph_attributes = generate_attributes_object(graph_type, graph_data, i);
		let style_attributes = generate_style_attributes(trial_data, i)
		let distractor_attribtues = generate_distractor_attributes(experiment, trial_data, i)
		let attributes = {dataset: dataset, graph_attributes: graph_attributes}
		switch(graph_type){
			case "scatter":
				create_scatter_plot(attributes, style_attributes, distractor_attribtues);
				break;
			case "strip":
				create_strip_plot(attributes, style_attributes, distractor_attribtues);
				break;
			case "parallel":
				create_parallel_plot(attributes, style_attributes, distractor_attribtues);
				break;
			default:
				throw Error("Graph Type Not Supported"); 
		}
	}

    // Set background color
    document.body.style.backgroundColor = (trial_data.background_color ? trial_data.background_color : "WHITE");
}

/**
 * 
 * @param {object} experiment 
 * @param {assoc array} trial_data current sub condition constants
 * @param {array[]} datasets array of the data coordinates which are 
 * @param {number} i current graph (0 is the left/top most)
 * @returns {assoc array}
 */
function generate_graphing_attributes(experiment, trial_data, datasets, i){

	let graph_data = trial_data.graph_a_attributes ? trial_data[experiment.graph_properties[i]] : trial_data;
	let dataset = datasets[i];
	let graph_attributes = generate_attributes_object(experiment.graph_type, graph_data, i);
	let attributes = {dataset: dataset, graph_attributes: graph_attributes};
	return attributes;
}

/**
 * Generates the style_attributes object for the graph which contains html alignment
 * and margin values.
 * Style Attribtues are generated by the get_mixed_graph_styles function in experiment helper
 * They help to line up the graph and distractor dimensions by having the same css style
 * This is not generalized for more than 2 graphs
 * 
 * @param {assoc array} trial_data current sub condition constants
 * @param {number} i current graph (0 is the left/top most)
 * @returns {assoc array}
 */
function generate_style_attributes(trial_data, i){
	let style_attributes = null;
	if(trial_data.style_attributes){
		style_attributes = trial_data["style_attributes"][i]
	}
	return style_attributes;
}

/**
 * Generates the distractor attributes for the experiment if they exist.
 * 
 * @param {object} experiment 
 * @param {number[]} trial_data 
 * @param {number} i 
 * @returns {assoc array}
 */
function generate_distractor_attributes(experiment, trial_data, i){
	let distractor_attribtues = null;
	if(trial_data["dist_attributes"]){
		let distractor_coordinates = experiment.distractor_coordinates;
		distractor_attribtues = {
			dataset: distractor_coordinates[i],
			graph_attributes: generate_attributes_object(experiment.graph_type, trial_data.dist_attributes, i)
		  }
	}
	return distractor_attribtues;
}


/**
 * Generates the js object for graph_attributes based on what is specified in
 * the graphing-config.js.
 *
 * E.g., will return object in this format:
 * {
 *	"axis_color"  : "BLACK",
 *  "point_color" : "RED",
 *  "point_size"  : 3
 *   ....
 * }
 *
 * @param {string}   plot_type			Only takes in graphs defined in GRAPH_TYPES
 * @param {assoc array}   trial_data         Trial data object from experiment model
 * @param {number}	plot_number		Denotes plot number from left to right (e.g. leftmost is 1 etc.)
 *
 * @return {assoc array}  graph_attributes
 */
export function generate_attributes_object(plot_type, trial_data, plot_number) {

	let curr_graph_attributes = {};
	const graph = GRAPH_TYPES[plot_type]; //GRAPH_TYPES comes from /config/graphing-config.js
	const graph_attributes = graph["attributes"];
	curr_graph_attributes = append_graph_attributes(trial_data, graph_attributes, curr_graph_attributes, plot_number);

	curr_graph_attributes = append_graph_feature_attributes(trial_data, curr_graph_attributes);
	
	return curr_graph_attributes;
}

/**
 * Appends the graph attributes to the curr_graph_attributes object
 * 
 * @param {assoc array} trial_data - current sub condition trial data
 * @param {assoc array} graph_attributes - graph attributes object for current graph being plotted
 * @param {assoc array} curr_graph_attributes - current graph attributes object to be passed to plotter
 * @param {number} plot_number - plot number for shapes case
 * @returns {assoc array}
 */
function append_graph_attributes(trial_data, graph_attributes, curr_graph_attributes, plot_number){
	console.log(trial_data)
	
	for (let key in graph_attributes) {

		const curr_attribute = graph_attributes[key];

		// These attributes are dependent on plot_number:
		if (key === "shapes") {

			curr_graph_attributes[key] = (key in trial_data ? trial_data[key][plot_number] : curr_attribute["default"])

		} else {
			// Check if the key exists in trial_data
			// If it exists, use the value in trial_data
			// Else, use the default specified
			curr_graph_attributes[key] = (key in trial_data ? trial_data[key] : curr_attribute["default"]);
		}
	}
	return curr_graph_attributes;
}

/**
 * Appends the graph feature attributes to the trial data, using the default
 * values if they are not present.
 * 
 * @param {assoc array} trial_data 
 * @param {assoc array} curr_graph_attributes 
 * @returns {assoc array}
 */
function append_graph_feature_attributes(trial_data, curr_graph_attributes){
	for (let key in GRAPH_FEATURES){
		const curr_feature_attributes = GRAPH_FEATURES[key]["attributes"];
		for(let feature_key in curr_feature_attributes){
			curr_graph_attributes[feature_key] = feature_key in trial_data ? trial_data[feature_key] : curr_feature_attributes[feature_key]["default"];
		}
	}
	return curr_graph_attributes;
}

