import Ensemble_Average from '/scripts/experiments/ensemble_average/ensemble_average.js';
import { get_instructions } from '/scripts/experiment-properties/instructions/instructions_controller.js';
import { resolution_scaling } from '/scripts/experiment-timeline/resolution_scaling/resolution_scaling.js';
import * as screening from '/scripts/experiment-timeline/screening/screening.js';

export const ensemble_avg_exp = new Ensemble_Average(params);

let timeline = [];

// =========================================================
// RESOLUTION SCALING
timeline.push(resolution_scaling);

// =========================================================
// SCREENING
timeline.push(
	screening.screeningQuestions,
	screening.screeningInstructions,
	screening.screeningCorrelationSort,
	screening.screeningCorrelationSelect,
	screening.instructionsRegression,
	screening.screeningRegressionSelect
);

// =========================================================
// INSTRUCTION
const instructions = {
	type: 'html-keyboard-response',
	stimulus: function() {
		return get_instructions(ensemble_avg_exp);
	}
};

timeline.push(instructions);

// =========================================================
// START LOOP

const cue = ensemble_avg_exp.generate_cue();

const fixation_point = {
	type: 'html-keyboard-response',
	stimulus: '<div><h1>+</h1></div>',
	choices: [ 't' ],
	trial_duration: 1000,
	data: { type: 'instruction' }
};

const trial = ensemble_avg_exp.generate_trial();

const averaging_task = {
	timeline: [ ensemble_avg_exp.generate_averaging_task() ],
	loop_function: function(data) {
		if (32 == data.values()[0].key_press) {
			return false;
		} else {
			return true;
		}
	}
};

const correlation_probe = ensemble_avg_exp.generate_correlation_probe();
const size_probe = ensemble_avg_exp.generate_size_probe();
const confidence_check = ensemble_avg_exp.generate_confidence_check();

const experiment = {
	timeline: [ cue, fixation_point, trial, averaging_task, correlation_probe, size_probe, confidence_check ],
	loop_function: function(data) {
		// For debugging, if you want to exit out of experiment, press q:
		if (jsPsych.pluginAPI.convertKeyCharacterToKeyCode('q') == data.values()[0].key_press) {
			return false;
		}

		if (!ensemble_avg_exp.trial_is_over()) {
			ensemble_avg_exp.go_next_trial();
			return true;
		} else {
			return false;
		}
	}
};

timeline.push(experiment);

