const JND_BLOCK_SIZE = 3;
const STEVENS_BLOCK_SIZE = 1;

// =========================================================
// INITIALIZING EXPERIMENT OBJECTS 
export let hybrid_exp = new Hybrid(params);
params["custom_subcondition_order"] = hybrid_exp.custom_order_string;
