//console.log(hybrid_exp.super_condition_constants)

let timeline = [];
const address = location.protocol + "//" + location.hostname + ":" + location.port;

const STEVENS_EXCLUSION_VALUE = 0.75;

const isPractice = hybrid_exp.is_practice_trials;
const isScreening = hybrid_exp.is_screening;
const manual_scaling_factor = hybrid_exp.manual_scaling_factor;

jnd_exp.is_hybrid = true;
stevens_exp.is_hybrid = true;

