  }
}

let stevens_instructions = get_jspsych_instructions(stevens_exp);

let jnd_instructions = get_jspsych_instructions(jnd_exp);

// CONDITIONAL: different timelines
var instruction_trials;
if (isPractice == "true") {
  let sample_steven_trials = stevens_exp.prepare_sample_trials();
  let steven_sample_explanation = {
    type: "html-keyboard-response",
