  generate_trial(block_type) {

    if ((block_type !== "test") && (block_type !== "practice")) {throw Error(block_type + " is not supported.")};

    // Initialize a variable for this so it is usable inside on_start
    var sequencing_side_by_side_exp = this;
    var address = location.protocol + "//" + location.hostname + ":" + location.port + "/sequencing_side_by_side_trial";

    // local variables so likert trial can access it too
