          var index = sequencing_side_by_side_exp.current_practice_condition_index;
        }
        subcond_num.stimulus = `<div align="center" style="font-size:20px"><p><b>Ready? Experiment block ${index + 1} of ${sequencing_side_by_side_exp.raw_constants.length} will now begin.</b></p><br><p>Press the spacebar to continue.</p></div>`;
      }
    }

    // conditional timeline wrapper
    var subcond_num_timeline = {
