          return true;
        } else {
          return false;
        }
      }
    }

    // "First" trial in the sequence. Contains the two side-by-side plots.
    var first = {
      type:'sequencing-graph',
      block_type: block_type,
      //url: address,
      choices: jsPsych.NO_KEYS,
      //execute_script: true,
      //trial_duration: 1000,

      on_start: function(first){
        sequencing_side_by_side_exp.cur_exp_type = block_type;
        if (block_type == "test"){
          index = sequencing_side_by_side_exp.current_sub_condition_index;
          constants = sequencing_side_by_side_exp.sub_conditions_constants[index];
          sequencing_side_by_side_exp.practice_end = true;
        }
        else {
