        console.log("Adjusted Value: " + adjusted_value);
        // Save data into matrix and jsPsych data object
        sequencing_side_by_side_exp.handle_data_saving(first, block_type, constants, index, adjusted_value, "first");

        // Generate distributions
        var base_coordinates = generate_initial_distribution(constants);
        var adjusted_coordinates = generate_adjusted_distribution(constants, adjusted_value);

        // Randomize position of the base and adjusted graphs
        var result = randomize_position(first,
                                       base_coordinates,
                                       adjusted_coordinates,
                                       constants,
