        // Set up D3 variables for plotting
        sequencing_side_by_side_exp.coordinates = [result.left, result.right];


        sequencing_side_by_side_exp.trial_data = first.data;

        // Custom jsPsych type requirement
        first.exp_obj = sequencing_side_by_side_exp;

        // Console log
        if (constants.task) {
          console.log("[TASK TYPE]: " + constants.task);
        }
        console.log("[RIGHT] Correlation: " + first.data.right_correlation);
        console.log("[RIGHT] Num points: " + first.data.right_num_points);

        console.log("[LEFT] Correlation: " + first.data.left_correlation);
        console.log("[LEFT] Num points: " + first.data.left_num_points);
      },
    };
    var distractor_size = sequencing_side_by_side_exp.getScreenHeight() / (4.3)
    console.log(distractor_size)
    var distractor = {
      type: 'html-keyboard-response',
      stimulus: `<div align = "center">` + `<img src="${IMAGE_PATH}" width="${distractor_size}px" style="margin: 0px 50px;" ></img><img src="${IMAGE_PATH}" width="${distractor_size}px" style="margin: 50px 0px;"></img><br><br><br></div>`,
      choices: jsPsych.NO_KEYS,
