import Sequencing_Side_by_Side from "/scripts/experiments/sequencing_side_by_side/sequencing_side_by_side.js";
import {IMAGE_PATH} from "/scripts/experiments/sequencing_side_by_side/sequencing_side_by_side.js";
import {get_instructions} from "/scripts/experiment-properties/instructions/instructions_controller.js";
import { resolution_scaling } from '/scripts/experiment-timeline/resolution_scaling/resolution_scaling.js';
import * as screening from '/scripts/experiment-timeline/screening/screening.js';

export var sequencing_side_by_side_exp = new Sequencing_Side_by_Side(params);

var timeline = [];
var address = location.protocol + "//" + location.hostname + ":" + location.port;

// =========================================================
// WELCOME TRIAL BLOCK

var welcome = {
  type: 'html-keyboard-response',
  stimulus: `<div align = "center">` + `<img src="${address}/img/VCL_lab_logo.png"></img><br><br>` +
            `<b>Base:</b> Sequencing` + '<br>' +
            `<b>Trial Type:</b> ${sequencing_side_by_side_exp.trial_structure}` + '<br>' +
            `<b>Graph Type:</b> ${sequencing_side_by_side_exp.graph_type}` + '<br>' +
            `<b>Condition:</b> ${sequencing_side_by_side_exp.condition_name}` +
            '<br><br><br><p><font size = 15>Press any key to begin.<p></font>' +
            '</div>',
  data: {type: 'instruction'}
};
timeline.push(welcome);


// =========================================================
// RESOLUTION SCALING

timeline.push(resolution_scaling);


// =========================================================
// SCREENING
//
// timeline.push(
// 	screening.screeningQuestions,
// 	screening.screeningInstructions,
// 	screening.screeningCorrelationSort,
// 	screening.screeningCorrelationSelect,
// 	screening.instructionsRegression,
// 	screening.screeningRegressionSelect
// );
//
// =========================================================
// INSTRUCTION TRIAL BLOCKS

var ready = {
  type: 'html-keyboard-response',
  stimulus: "<div align = 'center'> <font size = 20><p>Ready? We will first do some practice trials. <p>" + "<br><br><p><b>Press any key to begin.</b></p></font></div>",
  data: {type: 'instruction'}
}

var instructions = {
    type: "html-keyboard-response",
    stimulus: function(){
        return get_instructions(sequencing_side_by_side_exp);
    }
};

var instruction_trials = {
  timeline: [instructions, ready]
};

timeline.push(instruction_trials);

// =========================================================
// PRACTICE TRIAL BLOCKS

// ---------------------------------------------------------
// FEEDBACK

var feedback = {
  type: 'html-keyboard-response',
  choices: ['q'],
  post_trial_gap: 300,
  // trial_duration: 500,
  data: {type: 'feedback'},
  trial_duration: 500,
  on_start: function(feedback) {
    if (sequencing_side_by_side_exp.auto_mode) {
      feedback.trial_duration = 0;
    }
  },
  stimulus: function(){

    document.body.style.backgroundColor = sequencing_side_by_side_exp.trial_data.feedback_background_color;

    var last_trial = JSON.parse(jsPsych.data.getLastTrialData().json());
    var last_trial_correct = last_trial[0]["correct"];

    // For debugging purposes:
    if (last_trial_correct == -1){
      return '<p>' +
             '<font style="font-size:50px; color:blue">Exiting from experiment.<p></font>'
    }
    else if (last_trial_correct == -2){
      return '<p>' +
             '<font style="font-size:50px; color:blue">Skipping from subcondtion.<p></font>'
    }

    else if (last_trial_correct){
      return `<div align = "center">
              <p>
                <i class="fa fa-check-circle" style="font-size:50px; color:green; margin-right: 10px;"></i>
                <font style="font-size:50px; color:green">
                Correct!
                </font>
              <p>
              <br>
              </div>`
    }
    else{
      return `<div align = "center">
              <p>
                <i class="fa fa-close" style="font-size:50px; color:red; margin-right: 10px;"></i>
                <font style="font-size:50px; color:red;"">
                Incorrect!
                </font>
              <p>
              <br>
              </div>`
    }
  }
};

var feedback_wrapper = {
  timeline: [feedback],
  conditional_function: function() {
    if (sequencing_side_by_side_exp.end_sub_condition()) {
      return false;
    } else {
      return true;
    }
  },
}

// ---------------------------------------------------------
// PRE-SUBCONDITION INSTRUCTION BLOCK
var practice_sequencing = sequencing_side_by_side_exp.generate_trial("practice");

// This is a conditional block that will execute only if
// the subcondition has a subcondition_instructions attribute
// and it is the start of the subcondition.
var subcond_practice_instruction = {
  type: 'html-keyboard-response',
  stimulus: function(){

    let index = sequencing_side_by_side_exp.current_practice_condition_index;
    let constants = sequencing_side_by_side_exp.practice_conditions_constants[index];

    return constants.subcondition_instructions;
  }
}

var subcond_practice_instruction_block = {
  timeline: [subcond_practice_instruction],
  conditional_function: function() {

    let index = sequencing_side_by_side_exp.current_practice_condition_index;
    let constants = sequencing_side_by_side_exp.practice_conditions_constants[index];

    if (constants.subcondition_instructions){
      return true;
    } else {
      return false;
    }
  }
}

// ---------------------------------------------------------
// PRACTICE BLOCK

var practice = {
  timeline: [subcond_practice_instruction_block, ...practice_sequencing, feedback], // We use same feedback block as that used in practice
  loop_function: function(data){ // Return true if timeline should continue
                                 // Return false if timeline should end


    let key_presses = [];
    data.values().forEach(function(elem){
                          key_presses.push(elem.key_press);
                          });

    // For debugging, if you want to exit out of experiment, press q:
    if (key_presses.includes(jsPsych.pluginAPI.convertKeyCharacterToKeyCode('q'))){
      // Turn flag on
      sequencing_side_by_side_exp.first_trial_of_sub_condition = true;
      sequencing_side_by_side_exp.converged = false;
      sequencing_side_by_side_exp.end_sub_condition_debug = false;
      return false;
    }

    if(sequencing_side_by_side_exp.end_sub_condition()){
      // End practice
      sequencing_side_by_side_exp.first_trial_of_sub_condition = true;
      sequencing_side_by_side_exp.converged = false;
